"use client"

import { cn } from "../../lib/utils"
import { useDashboardStore } from "../../store/dashboard-store"
import { AlertBanner } from "./alert-banner"
import { EmployeesTable } from "./employees-table"
import { FinancialFlowChart } from "./financial-flow-chart"
import { StatsCards } from "./stats-cards"

export function DashboardContent() {
  const showAlertBanner = useDashboardStore((state) => state.showAlertBanner)
  const showStatsCards = useDashboardStore((state) => state.showStatsCards)
  const showChart = useDashboardStore((state) => state.showChart)
  const showTable = useDashboardStore((state) => state.showTable)
  const layoutDensity = useDashboardStore((state) => state.layoutDensity)

  return (
    <main
      className={cn(
        "w-full flex-1 overflow-auto",
        layoutDensity === "compact" && "space-y-4 p-2 sm:p-4",
        layoutDensity === "default" && "space-y-6 p-4 sm:space-y-8 sm:p-6",
        layoutDensity === "comfortable" && "space-y-8 p-6 sm:space-y-10 sm:p-8"
      )}
    >
      {showAlertBanner && <AlertBanner />}
      {showStatsCards && <StatsCards />}
      {showChart && <FinancialFlowChart />}
      {showTable && <EmployeesTable />}
    </main>
  )
}

"use client"

import * as React from "react"
import {
  ArrowLeft01Icon,
  ArrowRight01Icon,
  FileImportIcon,
  FilterIcon,
  Search01Icon,
  UserGroupIcon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { Avatar, AvatarFallback, AvatarImage } from "@loveui/ui/ui/avatar"
import { Button } from "@loveui/ui/ui/button"
import { Checkbox } from "@loveui/ui/ui/checkbox"
import { Input } from "@loveui/ui/ui/input"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@loveui/ui/ui/table"

import { cn } from "../../lib/utils"
import { employees, type Employee } from "../../mock-data/employees"
import { useDashboardStore } from "../../store/dashboard-store"

const PAGE_SIZE_OPTIONS = [8, 15, 25, 50]

const statusColors: Record<
  Employee["status"],
  { bg: string; text: string; border: string }
> = {
  Active: {
    bg: "bg-emerald-50 dark:bg-emerald-950/30",
    text: "text-emerald-600 dark:text-emerald-400",
    border: "border-emerald-200 dark:border-emerald-800",
  },
  "On Leave": {
    bg: "bg-amber-50 dark:bg-amber-950/30",
    text: "text-amber-600 dark:text-amber-400",
    border: "border-amber-200 dark:border-amber-800",
  },
  Probation: {
    bg: "bg-blue-50 dark:bg-blue-950/30",
    text: "text-blue-600 dark:text-blue-400",
    border: "border-blue-200 dark:border-blue-800",
  },
  Inactive: {
    bg: "bg-red-50 dark:bg-red-950/30",
    text: "text-red-600 dark:text-red-400",
    border: "border-red-200 dark:border-red-800",
  },
}

export function EmployeesTable() {
  const searchQuery = useDashboardStore((state) => state.searchQuery)
  const departmentFilter = useDashboardStore((state) => state.departmentFilter)
  const statusFilter = useDashboardStore((state) => state.statusFilter)
  const setSearchQuery = useDashboardStore((state) => state.setSearchQuery)
  const setDepartmentFilter = useDashboardStore(
    (state) => state.setDepartmentFilter
  )
  const setStatusFilter = useDashboardStore((state) => state.setStatusFilter)
  const clearFilters = useDashboardStore((state) => state.clearFilters)

  const [currentPage, setCurrentPage] = React.useState(1)
  const [pageSize, setPageSize] = React.useState(8)
  const [selectedRows, setSelectedRows] = React.useState<Set<string>>(new Set())

  const hasActiveFilters = departmentFilter !== "all" || statusFilter !== "all"

  const filteredEmployees = React.useMemo(() => {
    return employees.filter((emp) => {
      const matchesSearch =
        emp.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emp.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emp.userId.toLowerCase().includes(searchQuery.toLowerCase())

      const matchesDepartment =
        departmentFilter === "all" || emp.department === departmentFilter

      const matchesStatus =
        statusFilter === "all" || emp.status === statusFilter

      return matchesSearch && matchesDepartment && matchesStatus
    })
  }, [searchQuery, departmentFilter, statusFilter])

  const totalPages = Math.ceil(filteredEmployees.length / pageSize)

  const paginatedEmployees = React.useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize
    return filteredEmployees.slice(startIndex, startIndex + pageSize)
  }, [filteredEmployees, currentPage, pageSize])

  React.useEffect(() => {
    setCurrentPage(1)
  }, [searchQuery, departmentFilter, statusFilter, pageSize])

  const toggleSelectAll = () => {
    if (selectedRows.size === paginatedEmployees.length) {
      setSelectedRows(new Set())
    } else {
      setSelectedRows(new Set(paginatedEmployees.map((e) => e.id)))
    }
  }

  const toggleSelectRow = (id: string) => {
    const newSet = new Set(selectedRows)
    if (newSet.has(id)) {
      newSet.delete(id)
    } else {
      newSet.add(id)
    }
    setSelectedRows(newSet)
  }

  return (
    <div className="overflow-hidden rounded-xl border bg-card">
      <div className="flex flex-col justify-between gap-3 border-b px-5 py-4 sm:flex-row sm:items-center">
        <div className="flex items-center gap-2">
          <HugeiconsIcon
            icon={UserGroupIcon}
            className="size-5 text-muted-foreground"
          />
          <span className="font-medium text-muted-foreground">
            Employee list
          </span>
        </div>

        <div className="flex flex-wrap items-center gap-2">
          <div className="relative flex-1 sm:flex-none">
            <HugeiconsIcon
              icon={Search01Icon}
              className="pointer-events-none absolute top-1/2 left-3 z-10 size-4 -translate-y-1/2 text-muted-foreground"
            />
            <Input
              placeholder="Search Anything..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="h-9 w-full pl-9 sm:w-[220px]"
            />
          </div>

          <DropdownMenu>
            <DropdownMenuTrigger
              className={cn(
                "inline-flex h-9 items-center justify-center gap-2 rounded-md border px-3 text-sm font-medium",
                "border-border bg-muted shadow-xs hover:bg-background"
              )}
            >
              <HugeiconsIcon icon={FilterIcon} className="size-4" />
              Filter
              {hasActiveFilters && (
                <span className="size-1.5 rounded-full bg-primary" />
              )}
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-[200px]">
              <DropdownMenuGroup>
                <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Department
                </p>
                {[
                  "all",
                  "Nursing",
                  "Clinical",
                  "Operations",
                  "Admin",
                  "Support",
                ].map((dept) => (
                  <DropdownMenuCheckboxItem
                    key={dept}
                    checked={departmentFilter === dept}
                    onCheckedChange={() => setDepartmentFilter(dept)}
                  >
                    {dept === "all" ? "All Departments" : dept}
                  </DropdownMenuCheckboxItem>
                ))}
              </DropdownMenuGroup>
              <DropdownMenuSeparator />
              <DropdownMenuGroup>
                <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Status
                </p>
                {["all", "Active", "On Leave", "Probation", "Inactive"].map(
                  (status) => (
                    <DropdownMenuCheckboxItem
                      key={status}
                      checked={statusFilter === status}
                      onCheckedChange={() => setStatusFilter(status)}
                    >
                      {status === "all" ? "All Statuses" : status}
                    </DropdownMenuCheckboxItem>
                  )
                )}
              </DropdownMenuGroup>
              {hasActiveFilters && (
                <>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem
                    onClick={clearFilters}
                    className="text-destructive"
                  >
                    Clear all filters
                  </DropdownMenuItem>
                </>
              )}
            </DropdownMenuContent>
          </DropdownMenu>

          <div className="hidden h-6 w-px bg-border sm:block" />

          <Button variant="outline" className="gap-2">
            <HugeiconsIcon icon={FileImportIcon} className="size-4" />
            Import
          </Button>
        </div>
      </div>

      <div className="overflow-x-auto">
        <Table>
          <TableHeader>
            <TableRow className="bg-muted/50 hover:bg-muted/50">
              <TableHead className="w-[50px]">
                <Checkbox
                  checked={
                    selectedRows.size === paginatedEmployees.length &&
                    paginatedEmployees.length > 0
                  }
                  onCheckedChange={toggleSelectAll}
                />
              </TableHead>
              <TableHead className="min-w-[100px] font-medium text-muted-foreground">
                User ID
              </TableHead>
              <TableHead className="min-w-[150px] font-medium text-muted-foreground">
                Name
              </TableHead>
              <TableHead className="hidden min-w-[200px] font-medium text-muted-foreground md:table-cell">
                Email Address
              </TableHead>
              <TableHead className="hidden min-w-[100px] font-medium text-muted-foreground lg:table-cell">
                Department
              </TableHead>
              <TableHead className="hidden min-w-[140px] font-medium text-muted-foreground lg:table-cell">
                Job Title
              </TableHead>
              <TableHead className="hidden min-w-[120px] font-medium text-muted-foreground sm:table-cell">
                Joined Date
              </TableHead>
              <TableHead className="min-w-[100px] font-medium text-muted-foreground">
                Status
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {paginatedEmployees.length === 0 ? (
              <TableRow>
                <TableCell
                  colSpan={8}
                  className="h-24 text-center text-muted-foreground"
                >
                  No employees found matching your filters.
                </TableCell>
              </TableRow>
            ) : (
              paginatedEmployees.map((employee) => (
                <TableRow key={employee.id}>
                  <TableCell>
                    <Checkbox
                      checked={selectedRows.has(employee.id)}
                      onCheckedChange={() => toggleSelectRow(employee.id)}
                    />
                  </TableCell>
                  <TableCell className="font-medium text-muted-foreground">
                    {employee.userId}
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2.5">
                      <Avatar className="size-6">
                        {employee.avatar ? (
                          <AvatarImage src={employee.avatar} />
                        ) : null}
                        <AvatarFallback className="text-[10px] font-semibold">
                          {employee.name
                            .split(" ")
                            .map((n) => n[0])
                            .join("")}
                        </AvatarFallback>
                      </Avatar>
                      <span className="font-medium">{employee.name}</span>
                    </div>
                  </TableCell>
                  <TableCell className="hidden text-muted-foreground md:table-cell">
                    {employee.email}
                  </TableCell>
                  <TableCell className="hidden lg:table-cell">
                    <span className="rounded-md bg-muted px-2 py-0.5 text-xs font-medium text-muted-foreground">
                      {employee.department}
                    </span>
                  </TableCell>
                  <TableCell className="hidden lg:table-cell">
                    <span className="rounded-md bg-muted px-2 py-0.5 text-xs font-medium text-muted-foreground">
                      {employee.jobTitle}
                    </span>
                  </TableCell>
                  <TableCell className="hidden text-muted-foreground sm:table-cell">
                    {employee.joinedDate}
                  </TableCell>
                  <TableCell>
                    <span
                      className={cn(
                        "inline-flex items-center gap-1 rounded-md border px-2 py-0.5 text-xs font-medium",
                        statusColors[employee.status].bg,
                        statusColors[employee.status].text,
                        statusColors[employee.status].border
                      )}
                    >
                      {employee.status}
                    </span>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>

      <div className="flex flex-col items-center justify-between gap-4 border-t px-5 py-4 sm:flex-row">
        <div className="flex items-center gap-6">
          <Button
            variant="outline"
            size="icon-sm"
            onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
            disabled={currentPage === 1}
          >
            <HugeiconsIcon icon={ArrowLeft01Icon} className="size-4" />
          </Button>

          <div className="flex items-center gap-1">
            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              let pageNum: number
              if (totalPages <= 5) {
                pageNum = i + 1
              } else if (currentPage <= 3) {
                pageNum = i + 1
              } else if (currentPage >= totalPages - 2) {
                pageNum = totalPages - 4 + i
              } else {
                pageNum = currentPage - 2 + i
              }

              if (i === 3 && totalPages > 5 && currentPage < totalPages - 2) {
                return (
                  <span key="ellipsis" className="px-3 py-1 text-sm">
                    ...
                  </span>
                )
              }

              if (i === 4 && totalPages > 5) {
                pageNum = totalPages
              }

              return (
                <Button
                  key={pageNum}
                  variant={currentPage === pageNum ? "secondary" : "ghost"}
                  size="icon-sm"
                  onClick={() => setCurrentPage(pageNum)}
                  className={cn(currentPage === pageNum && "bg-muted")}
                >
                  {pageNum}
                </Button>
              )
            })}
          </div>

          <Button
            variant="outline"
            size="icon-sm"
            onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
          >
            <HugeiconsIcon icon={ArrowRight01Icon} className="size-4" />
          </Button>
        </div>

        <div className="flex items-center gap-4">
          <span className="text-xs text-muted-foreground">
            Showing {(currentPage - 1) * pageSize + 1} to{" "}
            {Math.min(currentPage * pageSize, filteredEmployees.length)} of{" "}
            {filteredEmployees.length} entries
          </span>

          <DropdownMenu>
            <DropdownMenuTrigger className="inline-flex h-8 items-center justify-center gap-2 rounded-md border border-border bg-background px-2.5 text-sm font-medium shadow-xs hover:bg-muted">
              Show {pageSize}
              <HugeiconsIcon
                icon={ArrowRight01Icon}
                className="size-3 rotate-90"
              />
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              {PAGE_SIZE_OPTIONS.map((size) => (
                <DropdownMenuItem
                  key={size}
                  onClick={() => setPageSize(size)}
                  className={cn(pageSize === size && "bg-muted")}
                >
                  Show {size}
                </DropdownMenuItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </div>
  )
}

"use client"

import { useState } from "react"
import {
  Calendar01Icon,
  ChartAverageIcon,
  ChartBarLineIcon,
  ChartLineData01Icon,
  GridIcon,
  Invoice01Icon,
  MoreHorizontalIcon,
  RefreshIcon,
  Tick01Icon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"
import { useTheme } from "next-themes"
import {
  Area,
  AreaChart,
  Bar,
  BarChart,
  CartesianGrid,
  Line,
  LineChart,
  ResponsiveContainer,
  Tooltip,
  TooltipProps,
  XAxis,
  YAxis,
} from "recharts"

import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"

const fullYearData = [
  {
    month: "Jan",
    moneyIn: 198000,
    moneyOut: 142000,
    moneyInChange: 6.4,
    moneyOutChange: -2.1,
  },
  {
    month: "Feb",
    moneyIn: 205000,
    moneyOut: 151000,
    moneyInChange: 3.5,
    moneyOutChange: 6.3,
  },
  {
    month: "Mar",
    moneyIn: 226000,
    moneyOut: 158000,
    moneyInChange: 10.2,
    moneyOutChange: 4.6,
  },
  {
    month: "Apr",
    moneyIn: 232000,
    moneyOut: 163000,
    moneyInChange: 2.7,
    moneyOutChange: 3.1,
  },
  {
    month: "May",
    moneyIn: 241000,
    moneyOut: 169000,
    moneyInChange: 3.9,
    moneyOutChange: 3.6,
  },
  {
    month: "Jun",
    moneyIn: 248000,
    moneyOut: 172000,
    moneyInChange: 2.9,
    moneyOutChange: 1.8,
  },
  {
    month: "Jul",
    moneyIn: 256000,
    moneyOut: 178000,
    moneyInChange: 3.2,
    moneyOutChange: 3.4,
  },
  {
    month: "Aug",
    moneyIn: 264000,
    moneyOut: 181000,
    moneyInChange: 3.1,
    moneyOutChange: 1.7,
  },
  {
    month: "Sep",
    moneyIn: 258000,
    moneyOut: 176000,
    moneyInChange: -2.2,
    moneyOutChange: -2.8,
  },
  {
    month: "Oct",
    moneyIn: 271000,
    moneyOut: 184000,
    moneyInChange: 5.0,
    moneyOutChange: 4.5,
  },
  {
    month: "Nov",
    moneyIn: 279000,
    moneyOut: 191000,
    moneyInChange: 3.0,
    moneyOutChange: 3.8,
  },
  {
    month: "Dec",
    moneyIn: 292000,
    moneyOut: 198000,
    moneyInChange: 4.7,
    moneyOutChange: 3.7,
  },
]

type ChartType = "bar" | "line" | "area"
type TimePeriod = "3months" | "6months" | "year" | "q1" | "q2" | "q3" | "q4"

const periodLabels: Record<TimePeriod, string> = {
  "3months": "Last 3 Months",
  "6months": "Last 6 Months",
  year: "Full Year",
  q1: "Q1 (Jan-Mar)",
  q2: "Q2 (Apr-Jun)",
  q3: "Q3 (Jul-Sep)",
  q4: "Q4 (Oct-Dec)",
}

function getDataForPeriod(period: TimePeriod) {
  switch (period) {
    case "3months":
      return fullYearData.slice(-3)
    case "6months":
      return fullYearData.slice(-6)
    case "q1":
      return fullYearData.slice(0, 3)
    case "q2":
      return fullYearData.slice(3, 6)
    case "q3":
      return fullYearData.slice(6, 9)
    case "q4":
      return fullYearData.slice(9, 12)
    default:
      return fullYearData
  }
}

function CustomTooltip({
  active,
  payload,
  label,
}: TooltipProps<number, string>) {
  if (!active || !payload?.length) return null

  const moneyInData = payload.find((p) => p.dataKey === "moneyIn")
  const moneyOutData = payload.find((p) => p.dataKey === "moneyOut")
  const moneyIn = moneyInData?.value || 0
  const moneyOut = moneyOutData?.value || 0
  const moneyInChange =
    (moneyInData?.payload as { moneyInChange?: number })?.moneyInChange || 0
  const moneyOutChange =
    (moneyOutData?.payload as { moneyOutChange?: number })?.moneyOutChange || 0

  return (
    <div className="min-w-[160px] rounded-lg border border-border bg-popover p-3 shadow-lg">
      <p className="mb-3 text-sm font-medium text-foreground">{label}, 2025</p>
      <div className="space-y-2">
        <div className="flex items-center justify-between gap-4">
          <div className="flex items-center gap-2">
            <div className="size-2 rounded-full bg-emerald-500" />
            <span className="text-sm font-semibold text-foreground">
              ${(Number(moneyIn) / 1000).toFixed(0)}k
            </span>
          </div>
          <span
            className={`text-xs font-medium ${moneyInChange >= 0 ? "text-red-500" : "text-emerald-500"}`}
          >
            {moneyInChange >= 0 ? "‚Üò" : "‚Üó"}{" "}
            {Math.abs(moneyInChange).toFixed(1)}%
          </span>
        </div>
        <div className="flex items-center justify-between gap-4">
          <div className="flex items-center gap-2">
            <div className="size-2 rounded-full bg-[#162664] dark:bg-indigo-500" />
            <span className="text-sm font-semibold text-foreground">
              ${(Number(moneyOut) / 1000).toFixed(0)}k
            </span>
          </div>
          <span
            className={`text-xs font-medium ${moneyOutChange >= 0 ? "text-emerald-500" : "text-red-500"}`}
          >
            {moneyOutChange >= 0 ? "‚Üó" : "‚Üò"}{" "}
            {Math.abs(moneyOutChange).toFixed(1)}%
          </span>
        </div>
      </div>
    </div>
  )
}

export function FinancialFlowChart() {
  const { theme } = useTheme()
  const [chartType, setChartType] = useState<ChartType>("line")
  const [period, setPeriod] = useState<TimePeriod>("year")
  const [showGrid, setShowGrid] = useState(true)
  const [showMoneyIn, setShowMoneyIn] = useState(true)
  const [showMoneyOut, setShowMoneyOut] = useState(true)
  const [smoothCurve, setSmoothCurve] = useState(false)

  const isDark = theme === "dark"
  const axisColor = isDark ? "#71717a" : "#a1a1aa"
  const gridColor = isDark ? "#27272a" : "#e5e7eb"
  const moneyOutColor = isDark ? "#6366f1" : "#162664"

  const chartData = getDataForPeriod(period)

  const resetToDefault = () => {
    setChartType("line")
    setPeriod("year")
    setShowGrid(true)
    setShowMoneyIn(true)
    setShowMoneyOut(true)
    setSmoothCurve(false)
  }

  return (
    <div className="overflow-hidden rounded-xl border bg-card">
      <div className="flex flex-col justify-between gap-4 px-5 py-4 sm:flex-row sm:items-center">
        <div className="flex items-center gap-2">
          <HugeiconsIcon
            icon={Invoice01Icon}
            className="size-5 text-muted-foreground"
          />
          <span className="font-medium text-muted-foreground">
            Staffing Budget Flow
          </span>
        </div>

        <div className="flex flex-wrap items-center gap-4 sm:gap-6">
          <div className="hidden items-center gap-4 sm:flex">
            <div className="flex items-center gap-1.5">
              <div className="size-3 rounded-full bg-emerald-500" />
              <span className="text-xs font-medium text-muted-foreground">
                Budget Allocated
              </span>
            </div>
            <div className="flex items-center gap-1.5">
              <div className="size-3 rounded-full bg-[#162664] dark:bg-indigo-500" />
              <span className="text-xs font-medium text-muted-foreground">
                Budget Used
              </span>
            </div>
          </div>

          <DropdownMenu>
            <DropdownMenuTrigger className="inline-flex size-8 items-center justify-center rounded-md hover:bg-muted">
              <HugeiconsIcon icon={MoreHorizontalIcon} className="size-4" />
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-56">
              <DropdownMenuGroup>
                <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Chart Type
                </p>
                <DropdownMenuItem onClick={() => setChartType("bar")}>
                  <HugeiconsIcon
                    icon={ChartBarLineIcon}
                    className="mr-2 size-4"
                  />
                  Bar Chart
                  {chartType === "bar" && (
                    <HugeiconsIcon
                      icon={Tick01Icon}
                      className="ml-auto size-4"
                    />
                  )}
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => setChartType("line")}>
                  <HugeiconsIcon
                    icon={ChartLineData01Icon}
                    className="mr-2 size-4"
                  />
                  Line Chart
                  {chartType === "line" && (
                    <HugeiconsIcon
                      icon={Tick01Icon}
                      className="ml-auto size-4"
                    />
                  )}
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => setChartType("area")}>
                  <HugeiconsIcon
                    icon={ChartAverageIcon}
                    className="mr-2 size-4"
                  />
                  Area Chart
                  {chartType === "area" && (
                    <HugeiconsIcon
                      icon={Tick01Icon}
                      className="ml-auto size-4"
                    />
                  )}
                </DropdownMenuItem>
              </DropdownMenuGroup>

              <DropdownMenuSeparator />

              <DropdownMenuSub>
                <DropdownMenuSubTrigger>
                  <HugeiconsIcon
                    icon={Calendar01Icon}
                    className="mr-2 size-4"
                  />
                  Time Period
                </DropdownMenuSubTrigger>
                <DropdownMenuSubContent>
                  {(Object.keys(periodLabels) as TimePeriod[]).map((key) => (
                    <DropdownMenuItem key={key} onClick={() => setPeriod(key)}>
                      {periodLabels[key]}
                      {period === key && (
                        <HugeiconsIcon
                          icon={Tick01Icon}
                          className="ml-auto size-4"
                        />
                      )}
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuSubContent>
              </DropdownMenuSub>

              <DropdownMenuSeparator />

              <DropdownMenuGroup>
                <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Display Options
                </p>
                <DropdownMenuCheckboxItem
                  checked={showGrid}
                  onCheckedChange={setShowGrid}
                >
                  <HugeiconsIcon icon={GridIcon} className="mr-2 size-4" />
                  Show Grid Lines
                </DropdownMenuCheckboxItem>

                {(chartType === "line" || chartType === "area") && (
                  <DropdownMenuCheckboxItem
                    checked={smoothCurve}
                    onCheckedChange={setSmoothCurve}
                  >
                    <HugeiconsIcon
                      icon={ChartAverageIcon}
                      className="mr-2 size-4"
                    />
                    Smooth Curve
                  </DropdownMenuCheckboxItem>
                )}
              </DropdownMenuGroup>

              <DropdownMenuSeparator />

              <DropdownMenuGroup>
                <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Data Series
                </p>
                <DropdownMenuCheckboxItem
                  checked={showMoneyIn}
                  onCheckedChange={setShowMoneyIn}
                >
                  <div className="mr-2 size-3 rounded-full bg-emerald-500" />
                  Show Money In
                </DropdownMenuCheckboxItem>

                <DropdownMenuCheckboxItem
                  checked={showMoneyOut}
                  onCheckedChange={setShowMoneyOut}
                >
                  <div className="mr-2 size-3 rounded-full bg-[#162664] dark:bg-indigo-500" />
                  Show Money Out
                </DropdownMenuCheckboxItem>
              </DropdownMenuGroup>

              <DropdownMenuSeparator />

              <DropdownMenuItem onClick={resetToDefault}>
                <HugeiconsIcon icon={RefreshIcon} className="mr-2 size-4" />
                Reset to Default
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>

      <div className="h-[250px] px-2 pb-4 sm:h-[280px]">
        <ResponsiveContainer width="100%" height="100%">
          {chartType === "bar" ? (
            <BarChart data={chartData} barGap={4}>
              <defs>
                <linearGradient
                  id="moneyInGradient"
                  x1="0"
                  y1="0"
                  x2="0"
                  y2="1"
                >
                  <stop offset="0%" stopColor="#2d9f75" stopOpacity={1} />
                  <stop offset="100%" stopColor="#2d9f75" stopOpacity={0.6} />
                </linearGradient>
                <linearGradient
                  id="moneyOutGradient"
                  x1="0"
                  y1="0"
                  x2="0"
                  y2="1"
                >
                  <stop offset="0%" stopColor={moneyOutColor} stopOpacity={1} />
                  <stop
                    offset="100%"
                    stopColor={moneyOutColor}
                    stopOpacity={0.6}
                  />
                </linearGradient>
              </defs>
              {showGrid && (
                <CartesianGrid
                  strokeDasharray="0"
                  stroke={gridColor}
                  vertical={false}
                />
              )}
              <XAxis
                dataKey="month"
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 12 }}
                dy={10}
              />
              <YAxis
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 10 }}
                tickFormatter={(value) => `$${value / 1000}k`}
                width={50}
              />
              <Tooltip
                content={<CustomTooltip />}
                cursor={{ fill: "#f4f4f5", radius: 4 }}
              />
              {showMoneyIn && (
                <Bar
                  dataKey="moneyIn"
                  fill="url(#moneyInGradient)"
                  radius={[4, 4, 0, 0]}
                  maxBarSize={22}
                />
              )}
              {showMoneyOut && (
                <Bar
                  dataKey="moneyOut"
                  fill="url(#moneyOutGradient)"
                  radius={[4, 4, 0, 0]}
                  maxBarSize={22}
                />
              )}
            </BarChart>
          ) : chartType === "line" ? (
            <LineChart data={chartData}>
              {showGrid && (
                <CartesianGrid
                  strokeDasharray="3 3"
                  stroke={gridColor}
                  vertical={true}
                />
              )}
              <XAxis
                dataKey="month"
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 12 }}
                dy={10}
              />
              <YAxis
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 10 }}
                tickFormatter={(value) => `$${value / 1000}k`}
                width={50}
              />
              <Tooltip
                content={<CustomTooltip />}
                cursor={{ stroke: "#d4d4d8" }}
              />
              {showMoneyIn && (
                <Line
                  type={smoothCurve ? "monotone" : "linear"}
                  dataKey="moneyIn"
                  stroke="#2d9f75"
                  strokeWidth={2}
                  dot={false}
                  activeDot={{
                    r: 6,
                    fill: "#2d9f75",
                    stroke: "white",
                    strokeWidth: 2,
                  }}
                />
              )}
              {showMoneyOut && (
                <Line
                  type={smoothCurve ? "monotone" : "linear"}
                  dataKey="moneyOut"
                  stroke={moneyOutColor}
                  strokeWidth={2}
                  dot={false}
                  activeDot={{
                    r: 6,
                    fill: moneyOutColor,
                    stroke: "white",
                    strokeWidth: 2,
                  }}
                />
              )}
            </LineChart>
          ) : (
            <AreaChart data={chartData}>
              <defs>
                <linearGradient
                  id="moneyInAreaGradient"
                  x1="0"
                  y1="0"
                  x2="0"
                  y2="1"
                >
                  <stop offset="0%" stopColor="#2d9f75" stopOpacity={0.3} />
                  <stop offset="100%" stopColor="#2d9f75" stopOpacity={0.05} />
                </linearGradient>
                <linearGradient
                  id="moneyOutAreaGradient"
                  x1="0"
                  y1="0"
                  x2="0"
                  y2="1"
                >
                  <stop
                    offset="0%"
                    stopColor={moneyOutColor}
                    stopOpacity={0.3}
                  />
                  <stop
                    offset="100%"
                    stopColor={moneyOutColor}
                    stopOpacity={0.05}
                  />
                </linearGradient>
              </defs>
              {showGrid && (
                <CartesianGrid
                  strokeDasharray="0"
                  stroke={gridColor}
                  vertical={false}
                />
              )}
              <XAxis
                dataKey="month"
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 12 }}
                dy={10}
              />
              <YAxis
                axisLine={false}
                tickLine={false}
                tick={{ fill: axisColor, fontSize: 10 }}
                tickFormatter={(value) => `$${value / 1000}k`}
                width={50}
              />
              <Tooltip
                content={<CustomTooltip />}
                cursor={{ stroke: "#d4d4d8" }}
              />
              {showMoneyIn && (
                <Area
                  type={smoothCurve ? "monotone" : "linear"}
                  dataKey="moneyIn"
                  stroke="#2d9f75"
                  strokeWidth={2}
                  fill="url(#moneyInAreaGradient)"
                />
              )}
              {showMoneyOut && (
                <Area
                  type={smoothCurve ? "monotone" : "linear"}
                  dataKey="moneyOut"
                  stroke={moneyOutColor}
                  strokeWidth={2}
                  fill="url(#moneyOutAreaGradient)"
                />
              )}
            </AreaChart>
          )}
        </ResponsiveContainer>
      </div>
    </div>
  )
}

