"use client"

import { useState } from "react"
import { MoreVerticalIcon } from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"
import {
  Bar,
  BarChart,
  CartesianGrid,
  Cell,
  Line,
  LineChart,
  XAxis,
  YAxis,
} from "recharts"

import { Button } from "@loveui/ui/ui/button"
import {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@loveui/ui/ui/chart"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"

import {
  performanceChange,
  performanceChartData,
  performanceScore,
} from "../../mock-data/dashboard"

type ChartType = "bar" | "line"
type Period = "7d" | "30d"

const barColors = [
  "var(--muted-foreground)",
  "var(--muted-foreground)",
  "var(--foreground)",
  "var(--muted-foreground)",
  "var(--muted-foreground)",
  "var(--muted-foreground)",
]

const chartConfig = {
  value: {
    label: "Performance",
  },
}

export function PerformanceChart() {
  const [chartType, setChartType] = useState<ChartType>("bar")
  const [period, setPeriod] = useState<Period>("7d")
  const [showGrid, setShowGrid] = useState(true)
  const [smoothCurve, setSmoothCurve] = useState(true)

  const resetToDefault = () => {
    setChartType("bar")
    setPeriod("7d")
    setShowGrid(true)
    setSmoothCurve(true)
  }

  // Pour l'instant, on garde les mêmes données quel que soit period
  const data = performanceChartData

  return (
    <div className="overflow-hidden rounded-xl border border-border bg-card">
      <div className="flex flex-col gap-3 border-b px-4 py-3 sm:flex-row sm:items-center sm:justify-between">
        <h3 className="text-base font-medium">Course Completion Score</h3>
        <DropdownMenu>
          <DropdownMenuTrigger
            render={
              <Button variant="ghost" size="icon" className="size-8">
                <HugeiconsIcon icon={MoreVerticalIcon} className="size-4" />
              </Button>
            }
          />
          <DropdownMenuContent align="end" className="w-52">
            <DropdownMenuSub>
              <DropdownMenuSubTrigger>Chart Type</DropdownMenuSubTrigger>
              <DropdownMenuSubContent>
                <DropdownMenuItem onClick={() => setChartType("bar")}>
                  Bar Chart {chartType === "bar" && "✓"}
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => setChartType("line")}>
                  Line Chart {chartType === "line" && "✓"}
                </DropdownMenuItem>
              </DropdownMenuSubContent>
            </DropdownMenuSub>
            <DropdownMenuSub>
              <DropdownMenuSubTrigger>Time Period</DropdownMenuSubTrigger>
              <DropdownMenuSubContent>
                <DropdownMenuItem onClick={() => setPeriod("7d")}>
                  Last 7 days {period === "7d" && "✓"}
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => setPeriod("30d")}>
                  Last 30 days {period === "30d" && "✓"}
                </DropdownMenuItem>
              </DropdownMenuSubContent>
            </DropdownMenuSub>
            <DropdownMenuSeparator />
            <DropdownMenuCheckboxItem
              checked={showGrid}
              onCheckedChange={(value) => setShowGrid(!!value)}
            >
              Show Grid
            </DropdownMenuCheckboxItem>
            <DropdownMenuCheckboxItem
              checked={smoothCurve}
              onCheckedChange={(value) => setSmoothCurve(!!value)}
              disabled={chartType === "bar"}
            >
              Smooth Curve
            </DropdownMenuCheckboxItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem onClick={resetToDefault}>
              Reset to Default
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
      <div className="p-4">
        <div className="mb-4 flex items-baseline gap-2">
          <span className="text-3xl font-semibold">{performanceScore}%</span>
          <span className="text-sm text-muted-foreground">
            +{performanceChange}% vs previous week
          </span>
        </div>
        <ChartContainer config={chartConfig} className="h-[175px] w-full">
          {chartType === "bar" ? (
            <BarChart
              data={data}
              margin={{ top: 8, right: 8, left: 8, bottom: 0 }}
            >
              {showGrid && (
                <CartesianGrid
                  strokeDasharray="3 3"
                  stroke="var(--border)"
                  vertical={false}
                />
              )}
              <XAxis
                dataKey="day"
                tickLine={false}
                axisLine={false}
                tick={{ fontSize: 11 }}
              />
              <YAxis hide domain={[0, 100]} />
              <ChartTooltip content={<ChartTooltipContent />} />
              <Bar dataKey="value" radius={[4, 4, 0, 0]} strokeWidth={0}>
                {data.map((entry, index) => (
                  <Cell key={entry.day} fill={barColors[index]} />
                ))}
              </Bar>
            </BarChart>
          ) : (
            <LineChart
              data={data}
              margin={{ top: 8, right: 8, left: 8, bottom: 0 }}
            >
              {showGrid && (
                <CartesianGrid
                  strokeDasharray="3 3"
                  stroke="var(--border)"
                  vertical={false}
                />
              )}
              <XAxis
                dataKey="day"
                tickLine={false}
                axisLine={false}
                tick={{ fontSize: 11 }}
              />
              <YAxis hide domain={[0, 100]} />
              <ChartTooltip content={<ChartTooltipContent />} />
              <Line
                type={smoothCurve ? "monotone" : "linear"}
                dataKey="value"
                stroke="var(--foreground)"
                strokeWidth={2}
                dot={false}
                activeDot={{
                  r: 6,
                  fill: "var(--foreground)",
                  stroke: "var(--card)",
                  strokeWidth: 2,
                }}
              />
            </LineChart>
          )}
        </ChartContainer>
      </div>
    </div>
  )
}

"use client"

import { useMemo, useState } from "react"
import {
  ArrowLeft01Icon,
  ArrowLeftDoubleIcon,
  ArrowRight01Icon,
  ArrowRightDoubleIcon,
  CircleIcon,
  Clock01Icon,
  Database01Icon,
  FilterIcon,
  Folder01Icon,
  Layers01Icon,
  Megaphone01Icon,
  Search01Icon,
  Tick01Icon,
  Wallet01Icon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"
import {
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  useReactTable,
} from "@tanstack/react-table"
import type { ColumnDef } from "@tanstack/react-table"

import { Avatar, AvatarFallback, AvatarImage } from "@loveui/ui/ui/avatar"
import { Button } from "@loveui/ui/ui/button"
import { Checkbox } from "@loveui/ui/ui/checkbox"
import { Input } from "@loveui/ui/ui/input"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"
import { Progress } from "@loveui/ui/ui/progress"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@loveui/ui/ui/select"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@loveui/ui/ui/table"

import { cn } from "../../lib/utils"
import {
  projects,
  type Project,
  type ProjectStatus,
} from "../../mock-data/dashboard"
import { useDashboardStore } from "../../store/dashboard-store"

function StatusBadge({ status }: { status: ProjectStatus }) {
  if (status === "in_progress") {
    return (
      <div className="flex items-center gap-1.5">
        <HugeiconsIcon
          icon={CircleIcon}
          className="size-3.5 fill-cyan-500 text-cyan-500"
        />
        <span className="text-sm text-cyan-600 dark:text-cyan-400">
          In Progress
        </span>
      </div>
    )
  }
  if (status === "completed") {
    return (
      <div className="flex items-center gap-1.5">
        <HugeiconsIcon
          icon={Tick01Icon}
          className="size-3.5 text-emerald-500"
        />
        <span className="text-sm text-emerald-600 dark:text-emerald-400">
          Completed
        </span>
      </div>
    )
  }
  return (
    <div className="flex items-center gap-1.5">
      <HugeiconsIcon icon={Clock01Icon} className="size-3.5 text-amber-500" />
      <span className="text-sm text-amber-600 dark:text-amber-400">
        On Hold
      </span>
    </div>
  )
}

const projectIconMap: Record<
  string,
  { icon: typeof Folder01Icon; iconColor: string }
> = {
  blue: { icon: Folder01Icon, iconColor: "text-blue-500" },
  violet: { icon: Layers01Icon, iconColor: "text-violet-500" },
  cyan: { icon: Database01Icon, iconColor: "text-cyan-500" },
  pink: { icon: Megaphone01Icon, iconColor: "text-pink-500" },
  amber: { icon: Wallet01Icon, iconColor: "text-amber-500" },
}

export function ProjectsTable() {
  const {
    projectsSearchQuery,
    setProjectsSearchQuery,
    projectStatusFilter,
    setProjectStatusFilter,
  } = useDashboardStore()

  const columns = useMemo<ColumnDef<Project>[]>(
    () => [
      {
        id: "select",
        header: ({ table }) => (
          <Checkbox
            checked={
              table.getIsAllPageRowsSelected()
                ? true
                : table.getIsSomePageRowsSelected()
                  ? true
                  : false
            }
            onCheckedChange={(value) =>
              table.toggleAllPageRowsSelected(!!value)
            }
            aria-label="Select all"
          />
        ),
        cell: ({ row }) => (
          <Checkbox
            checked={row.getIsSelected()}
            onCheckedChange={(value) => row.toggleSelected(!!value)}
            aria-label="Select row"
          />
        ),
        enableSorting: false,
        enableHiding: false,
      },
      {
        accessorKey: "name",
        header: "Project Name",
        cell: ({ row }) => {
          const p = row.original
          const { icon, iconColor } =
            projectIconMap[p.color] ?? projectIconMap.blue
          return (
            <div className="flex items-center gap-2 font-medium text-foreground">
              <HugeiconsIcon
                icon={icon}
                className={cn("size-4 shrink-0", iconColor)}
              />
              {p.name}
            </div>
          )
        },
      },
      {
        accessorKey: "status",
        header: "Status",
        cell: ({ row }) => <StatusBadge status={row.original.status} />,
      },
      {
        accessorKey: "progress",
        header: "Progress",
        cell: ({ row }) => (
          <div className="flex min-w-[100px] items-center gap-2">
            <Progress value={row.original.progress} className="h-2 flex-1" />
            <span className="w-8 text-sm tabular-nums">
              {row.original.progress}%
            </span>
          </div>
        ),
      },
      {
        id: "tasks",
        header: "Total Tasks",
        cell: ({ row }) => {
          const p = row.original
          return (
            <span className="text-sm text-muted-foreground">
              {p.completedTasks}/{p.totalTasks}
            </span>
          )
        },
      },
      {
        accessorKey: "dueDate",
        header: "Due Date",
        cell: ({ row }) => (
          <span className="text-sm text-muted-foreground">
            {row.original.dueDate}
          </span>
        ),
      },
      {
        accessorKey: "ownerName",
        header: "Owner",
        cell: ({ row }) => {
          const p = row.original
          return (
            <div className="flex items-center gap-2">
              <Avatar className="size-6">
                <AvatarImage
                  src={`https://api.dicebear.com/9.x/glass/svg?seed=${p.ownerAvatarSeed}`}
                />
                <AvatarFallback className="text-xs">
                  {p.ownerName.slice(0, 2)}
                </AvatarFallback>
              </Avatar>
              <span className="text-sm">{p.ownerName}</span>
            </div>
          )
        },
      },
    ],
    []
  )

  const filteredData = useMemo(() => {
    let result = projects
    if (projectsSearchQuery.trim()) {
      const q = projectsSearchQuery.toLowerCase()
      result = result.filter(
        (p) =>
          p.name.toLowerCase().includes(q) ||
          p.ownerName.toLowerCase().includes(q)
      )
    }
    if (projectStatusFilter !== "all") {
      result = result.filter((p) => p.status === projectStatusFilter)
    }
    return result
  }, [projectsSearchQuery, projectStatusFilter])

  const [rowSelection, setRowSelection] = useState({})

  const table = useReactTable({
    data: filteredData,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    onRowSelectionChange: setRowSelection,
    state: { rowSelection },
    initialState: {
      pagination: { pageSize: 10 },
    },
  })

  const hasActiveFilters = projectStatusFilter !== "all"
  const pageSize = table.getState().pagination.pageSize
  const pageIndex = table.getState().pagination.pageIndex
  const totalRows = filteredData.length
  const from = pageIndex * pageSize + 1
  const to = Math.min((pageIndex + 1) * pageSize, totalRows)

  return (
    <div className="overflow-hidden rounded-xl border border-border bg-card">
      <div className="flex flex-col gap-3 border-b px-4 py-3 sm:flex-row sm:items-center sm:justify-between">
        <h3 className="text-base font-medium">Course Board</h3>
        <div className="flex items-center gap-2">
          <div className="relative">
            <HugeiconsIcon
              icon={Search01Icon}
              className="pointer-events-none absolute top-1/2 left-2.5 z-10 size-4 -translate-y-1/2 text-muted-foreground"
            />
            <Input
              placeholder="Search courses..."
              value={projectsSearchQuery}
              onChange={(e) => setProjectsSearchQuery(e.target.value)}
              className="h-9 w-full bg-muted/50 pl-8 text-sm sm:w-[200px]"
            />
          </div>
          <DropdownMenu>
            <DropdownMenuTrigger
              render={
                <Button variant="outline" size="sm" className="h-9 gap-1.5">
                  <HugeiconsIcon icon={FilterIcon} className="size-4" />
                  Filter
                  {hasActiveFilters && (
                    <span className="size-1.5 rounded-full bg-primary" />
                  )}
                </Button>
              }
            />
            <DropdownMenuContent align="start" className="w-44">
              <DropdownMenuCheckboxItem
                checked={projectStatusFilter === "all"}
                onCheckedChange={() => setProjectStatusFilter("all")}
              >
                All statuses
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={projectStatusFilter === "in_progress"}
                onCheckedChange={() => setProjectStatusFilter("in_progress")}
              >
                In Progress
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={projectStatusFilter === "completed"}
                onCheckedChange={() => setProjectStatusFilter("completed")}
              >
                Completed
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={projectStatusFilter === "on_hold"}
                onCheckedChange={() => setProjectStatusFilter("on_hold")}
              >
                On Hold
              </DropdownMenuCheckboxItem>
              {hasActiveFilters && (
                <>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem
                    onClick={() => setProjectStatusFilter("all")}
                  >
                    Clear filter
                  </DropdownMenuItem>
                </>
              )}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
      <div className="overflow-x-auto">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id} className="hover:bg-transparent">
                {headerGroup.headers.map((header) => (
                  <TableHead key={header.id}>
                    {flexRender(
                      header.column.columnDef.header,
                      header.getContext()
                    )}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows.length === 0 ? (
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="py-8 text-center text-muted-foreground"
                >
                  No courses found.
                </TableCell>
              </TableRow>
            ) : (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() ? "selected" : undefined}
                >
                  {row.getVisibleCells().map((cell) => (
                    <TableCell key={cell.id}>
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>
      <div className="flex flex-col items-center justify-between gap-4 border-t px-4 py-3 sm:flex-row">
        <div className="flex items-center gap-4 text-sm text-muted-foreground">
          <span>
            {totalRows === 0
              ? "0 courses"
              : `Showing ${from} to ${to} of ${totalRows} courses`}
          </span>
          <div className="flex items-center gap-2">
            <span className="hidden sm:inline">Rows per page</span>
            <Select
              value={String(pageSize)}
              onValueChange={(v) => table.setPageSize(Number(v))}
            >
              <SelectTrigger className="h-8 w-[70px]">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="5">5</SelectItem>
                <SelectItem value="10">10</SelectItem>
                <SelectItem value="20">20</SelectItem>
                <SelectItem value="50">50</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
        <div className="flex items-center gap-1">
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => table.setPageIndex(0)}
            disabled={!table.getCanPreviousPage()}
          >
            <HugeiconsIcon icon={ArrowLeftDoubleIcon} className="size-4" />
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => table.previousPage()}
            disabled={!table.getCanPreviousPage()}
          >
            <HugeiconsIcon icon={ArrowLeft01Icon} className="size-4" />
          </Button>
          <span className="px-2 text-sm tabular-nums">
            {pageIndex + 1} / {table.getPageCount() || 1}
          </span>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => table.nextPage()}
            disabled={!table.getCanNextPage()}
          >
            <HugeiconsIcon icon={ArrowRight01Icon} className="size-4" />
          </Button>
          <Button
            variant="outline"
            size="icon"
            className="size-8"
            onClick={() => table.setPageIndex(table.getPageCount() - 1)}
            disabled={!table.getCanNextPage()}
          >
            <HugeiconsIcon icon={ArrowRightDoubleIcon} className="size-4" />
          </Button>
        </div>
      </div>
    </div>
  )
}

"use client"

import {
  Folder01Icon,
  Task01Icon,
  Tick01Icon,
  ViewIcon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { dashboardStats } from "../../mock-data/dashboard"

const stats = [
  {
    title: "Active Courses",
    value: dashboardStats.totalProjects.value,
    change: dashboardStats.totalProjects.change,
    icon: Folder01Icon,
  },
  {
    title: "Open Assignments",
    value: dashboardStats.totalTasks.value,
    change: dashboardStats.totalTasks.change,
    icon: Task01Icon,
  },
  {
    title: "Pending Grading",
    value: dashboardStats.inReviews.value,
    change: dashboardStats.inReviews.change,
    icon: ViewIcon,
  },
  {
    title: "Graded Items",
    value: dashboardStats.completedTasks.value,
    change: dashboardStats.completedTasks.change,
    icon: Tick01Icon,
  },
]

export function StatsCards() {
  return (
    <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
      {stats.map((stat, index) => (
        <div
          key={index}
          className="rounded-xl border border-border bg-card p-4"
        >
          <div className="flex items-start justify-between">
            <div className="space-y-1">
              <p className="text-sm text-muted-foreground">{stat.title}</p>
              <p className="text-2xl font-medium text-foreground">
                {stat.value}
              </p>
              <p className="text-xs text-muted-foreground">
                +{stat.change} vs last month
              </p>
            </div>
            <div className="flex size-10 shrink-0 items-center justify-center rounded-lg border border-border bg-muted">
              <HugeiconsIcon
                icon={stat.icon}
                className="size-5 text-muted-foreground"
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  )
}

"use client"

import { useMemo } from "react"
import { FilterIcon, Search01Icon } from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { Button } from "@loveui/ui/ui/button"
import { Input } from "@loveui/ui/ui/input"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"

import { cn } from "../../lib/utils"
import { todayTasks } from "../../mock-data/dashboard"
import { useDashboardStore } from "../../store/dashboard-store"

const projectColorMap: Record<string, string> = {
  blue: "rounded-lg border border-border bg-muted/50 text-foreground",
  violet: "rounded-lg border border-border bg-muted/50 text-foreground",
  cyan: "rounded-lg border border-border bg-muted/50 text-foreground",
  pink: "rounded-lg border border-border bg-muted/50 text-foreground",
  amber: "rounded-lg border border-border bg-muted/50 text-foreground",
}

const uniqueProjects = Array.from(
  new Map(
    todayTasks.map((t) => [
      t.projectId,
      { id: t.projectId, name: t.projectName },
    ])
  ).values()
)

export function TodaysTasks() {
  const {
    tasksSearchQuery,
    setTasksSearchQuery,
    tasksProjectFilter,
    toggleTasksProjectFilter,
    setTasksProjectFilter,
  } = useDashboardStore()

  const filteredTasks = useMemo(() => {
    let result = todayTasks
    if (tasksSearchQuery.trim()) {
      const q = tasksSearchQuery.toLowerCase()
      result = result.filter(
        (t) =>
          t.name.toLowerCase().includes(q) ||
          t.projectName.toLowerCase().includes(q)
      )
    }
    if (tasksProjectFilter.length > 0) {
      result = result.filter((t) => tasksProjectFilter.includes(t.projectId))
    }
    return result
  }, [tasksSearchQuery, tasksProjectFilter])

  const hasTaskFilters = tasksProjectFilter.length > 0

  return (
    <div className="overflow-hidden rounded-xl border border-border bg-card">
      <div className="flex flex-col gap-3 border-b px-4 py-3 sm:flex-row sm:items-center sm:justify-between">
        <h3 className="text-base font-medium">Today&apos;s Academic Tasks</h3>
        <div className="flex items-center gap-2">
          <div className="relative">
            <HugeiconsIcon
              icon={Search01Icon}
              className="pointer-events-none absolute top-1/2 left-2.5 z-10 size-4 -translate-y-1/2 text-muted-foreground"
            />
            <Input
              placeholder="Search here..."
              value={tasksSearchQuery}
              onChange={(e) => setTasksSearchQuery(e.target.value)}
              className="h-9 w-full bg-muted/50 pl-8 text-sm sm:w-[200px]"
            />
          </div>
          <DropdownMenu>
            <DropdownMenuTrigger
              render={
                <Button variant="outline" size="sm" className="h-9 gap-1.5">
                  <HugeiconsIcon icon={FilterIcon} className="size-4" />
                  Filter
                  {hasTaskFilters && (
                    <span className="size-1.5 rounded-full bg-primary" />
                  )}
                </Button>
              }
            />
            <DropdownMenuContent align="end" className="w-52">
              <DropdownMenuCheckboxItem
                checked={tasksProjectFilter.length === 0}
                onCheckedChange={() => setTasksProjectFilter([])}
              >
                All projects
              </DropdownMenuCheckboxItem>
              <DropdownMenuSeparator />
              {uniqueProjects.map((proj) => (
                <DropdownMenuCheckboxItem
                  key={proj.id}
                  checked={tasksProjectFilter.includes(proj.id)}
                  onCheckedChange={() => toggleTasksProjectFilter(proj.id)}
                >
                  {proj.name}
                </DropdownMenuCheckboxItem>
              ))}
              {hasTaskFilters && (
                <>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={() => setTasksProjectFilter([])}>
                    Clear filter
                  </DropdownMenuItem>
                </>
              )}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
      <div className="divide-y">
        {filteredTasks.length === 0 ? (
          <div className="px-4 py-8 text-center text-sm text-muted-foreground">
            No academic tasks match your search.
          </div>
        ) : (
          filteredTasks.map((task) => (
            <div
              key={task.id}
              className="flex flex-wrap items-center gap-3 px-4 py-3 transition-colors hover:bg-muted/30"
            >
              <span className="text-sm font-medium">{task.name}</span>
              <div
                className={cn(
                  "inline-flex items-center gap-1.5 px-2 py-1 text-xs font-medium",
                  projectColorMap[task.projectColor] ?? projectColorMap.blue
                )}
              >
                {task.projectName}
              </div>
              <span className="ml-auto text-xs text-muted-foreground">
                Due: {task.dueDate}
              </span>
            </div>
          ))
        )}
      </div>
    </div>
  )
}

