"use client"

import { Event } from "../../mock-data/events"
import {
  getEventHeight,
  getEventTop,
  HOUR_HEIGHT,
  HOURS_24,
} from "./calendar-utils"
import { CurrentTimeIndicator } from "./current-time-indicator"
import { EventCard } from "./event-card"

interface CalendarDayColumnProps {
  day: Date
  dayIndex: number
  events: Event[]
  today: Date
  isTodayInWeek: boolean
  currentTime: Date
  onScroll: (index: number) => (e: React.UIEvent<HTMLDivElement>) => void
  scrollRef: (el: HTMLDivElement | null) => void
  onEventClick: (event: Event) => void
}

export function CalendarDayColumn({
  day,
  dayIndex,
  events,
  today,
  isTodayInWeek,
  currentTime,
  onScroll,
  scrollRef,
  onEventClick,
}: CalendarDayColumnProps) {
  return (
    <div
      ref={scrollRef}
      onScroll={onScroll(dayIndex)}
      className="relative min-w-44 flex-1 overflow-y-auto border-r border-border last:border-r-0"
    >
      {HOURS_24.map((hour) => (
        <div
          key={hour}
          className="border-b border-border"
          style={{ height: `${HOUR_HEIGHT}px` }}
        />
      ))}

      <CurrentTimeIndicator
        day={day}
        today={today}
        isTodayInWeek={isTodayInWeek}
        currentTime={currentTime}
      />

      {events.map((event) => {
        const top = getEventTop(event.startTime)
        const height = getEventHeight(event.startTime, event.endTime)

        return (
          <EventCard
            key={event.id}
            event={event}
            style={{
              top: `${top + 4}px`,
              height: `${height - 8}px`,
            }}
            onClick={() => onEventClick(event)}
          />
        )
      })}
    </div>
  )
}

"use client"

import { HOUR_HEIGHT, HOURS_24 } from "./calendar-utils"

interface CalendarHoursColumnProps {
  onScroll: (e: React.UIEvent<HTMLDivElement>) => void
  scrollRef: React.RefObject<HTMLDivElement | null>
}

export function CalendarHoursColumn({
  onScroll,
  scrollRef,
}: CalendarHoursColumnProps) {
  return (
    <div
      ref={scrollRef}
      onScroll={onScroll}
      className="relative w-[80px] shrink-0 overflow-y-auto border-r border-border md:w-[104px]"
    >
      {HOURS_24.map((hour) => (
        <div
          key={hour}
          className="border-b border-border p-2 text-xs text-muted-foreground md:p-3 md:text-sm"
          style={{ height: `${HOUR_HEIGHT}px` }}
        >
          {hour}
        </div>
      ))}
    </div>
  )
}

export const HOURS_24 = Array.from({ length: 24 }, (_, i) => {
  if (i === 0) return "12 AM"
  if (i < 12) return `${i} AM`
  if (i === 12) return "12 PM"
  return `${i - 12} PM`
})

export const HOUR_HEIGHT = 120
export const INITIAL_SCROLL_OFFSET = 9 * HOUR_HEIGHT

export function getEventHeight(startTime: string, endTime: string): number {
  const [startHour, startMin] = startTime.split(":").map(Number)
  const [endHour, endMin] = endTime.split(":").map(Number)

  const startTotal = startHour * 60 + (startMin || 0)
  const endTotal = endHour * 60 + (endMin || 0)
  const duration = endTotal - startTotal

  if (duration < 60) {
    return Math.max(24, Math.round((duration / 60) * HOUR_HEIGHT))
  }

  return Math.max(40, Math.round((duration / 60) * HOUR_HEIGHT))
}

export function getEventTop(startTime: string): number {
  const [hour, minute] = startTime.split(":").map(Number)
  const totalMinutes = hour * 60 + (minute || 0)
  const offset = totalMinutes * (HOUR_HEIGHT / 60)
  return Math.max(0, Math.round(offset))
}

export function getCurrentTimePosition(date: Date = new Date()): number {
  const hour = date.getHours()
  const minute = date.getMinutes()
  const totalMinutes = hour * 60 + minute
  const offset = totalMinutes * (HOUR_HEIGHT / 60)
  return Math.max(0, Math.round(offset))
}

export function getEventDuration(startTime: string, endTime: string): number {
  const [startHour, startMin] = startTime.split(":").map(Number)
  const [endHour, endMin] = endTime.split(":").map(Number)
  const startTotal = startHour * 60 + (startMin || 0)
  const endTotal = endHour * 60 + (endMin || 0)
  return endTotal - startTotal
}

"use client"

import { useEffect, useRef, useState } from "react"
import { format } from "date-fns"

import { Event } from "../../mock-data/events"
import { useCalendarStore } from "../../store/calendar-store"
import { CalendarDayColumn } from "./calendar-day-column"
import { CalendarHoursColumn } from "./calendar-hours-column"
import { INITIAL_SCROLL_OFFSET } from "./calendar-utils"
import { CalendarWeekHeader } from "./calendar-week-header"
import { EventSheet } from "./event-sheet"

export function CalendarView() {
  const { goToNextWeek, goToPreviousWeek, getWeekDays, getCurrentWeekEvents } =
    useCalendarStore()
  const weekDays = getWeekDays()
  const events = getCurrentWeekEvents()
  const hoursScrollRef = useRef<HTMLDivElement>(null)
  const daysScrollRefs = useRef<(HTMLDivElement | null)[]>([])
  const hasScrolledRef = useRef(false)
  const [currentTime, setCurrentTime] = useState(new Date())
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null)
  const [sheetOpen, setSheetOpen] = useState(false)

  const today = new Date()

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date())
    }, 60000)

    return () => clearInterval(interval)
  }, [])

  const eventsByDay: Record<string, Event[]> = {}
  weekDays.forEach((day) => {
    const dayStr = format(day, "yyyy-MM-dd")
    eventsByDay[dayStr] = events.filter((e) => e.date === dayStr)
  })

  const isTodayInWeek = weekDays.some(
    (day) => format(day, "yyyy-MM-dd") === format(today, "yyyy-MM-dd")
  )

  useEffect(() => {
    const scrollToInitial = () => {
      if (!hasScrolledRef.current && hoursScrollRef.current) {
        hoursScrollRef.current.scrollTop = INITIAL_SCROLL_OFFSET
        daysScrollRefs.current.forEach((ref) => {
          if (ref) {
            ref.scrollTop = INITIAL_SCROLL_OFFSET
          }
        })
        hasScrolledRef.current = true
      }
    }

    scrollToInitial()
    const timeoutId = setTimeout(scrollToInitial, 100)
    return () => clearTimeout(timeoutId)
  }, [weekDays])

  const handleHoursScroll = (e: React.UIEvent<HTMLDivElement>) => {
    const scrollTop = e.currentTarget.scrollTop
    daysScrollRefs.current.forEach((ref) => {
      if (ref) {
        ref.scrollTop = scrollTop
      }
    })
  }

  const handleDayScroll =
    (index: number) => (e: React.UIEvent<HTMLDivElement>) => {
      const scrollTop = e.currentTarget.scrollTop
      if (hoursScrollRef.current) {
        hoursScrollRef.current.scrollTop = scrollTop
      }
      daysScrollRefs.current.forEach((ref, idx) => {
        if (ref && idx !== index) {
          ref.scrollTop = scrollTop
        }
      })
    }

  const handleEventClick = (event: Event) => {
    setSelectedEvent(event)
    setSheetOpen(true)
  }

  return (
    <>
      <EventSheet
        event={selectedEvent}
        open={sheetOpen}
        onOpenChange={setSheetOpen}
      />
      <div className="flex h-full w-full flex-col overflow-x-auto">
        <CalendarWeekHeader
          weekDays={weekDays}
          onPreviousWeek={goToPreviousWeek}
          onNextWeek={goToNextWeek}
        />

        <div className="flex w-max min-w-full">
          <CalendarHoursColumn
            onScroll={handleHoursScroll}
            scrollRef={hoursScrollRef}
          />

          {weekDays.map((day, dayIndex) => {
            const dayStr = format(day, "yyyy-MM-dd")
            const dayEvents = eventsByDay[dayStr] || []

            return (
              <CalendarDayColumn
                key={day.toISOString()}
                day={day}
                dayIndex={dayIndex}
                events={dayEvents}
                today={today}
                isTodayInWeek={isTodayInWeek}
                currentTime={currentTime}
                onScroll={handleDayScroll}
                scrollRef={(el) => {
                  daysScrollRefs.current[dayIndex] = el
                }}
                onEventClick={handleEventClick}
              />
            )
          })}
        </div>
      </div>
    </>
  )
}

"use client"

import { isSameDay } from "date-fns"

import { getCurrentTimePosition } from "./calendar-utils"

interface CurrentTimeIndicatorProps {
  day: Date
  today: Date
  isTodayInWeek: boolean
  currentTime: Date
}

export function CurrentTimeIndicator({
  day,
  today,
  isTodayInWeek,
  currentTime,
}: CurrentTimeIndicatorProps) {
  if (!isTodayInWeek || !isSameDay(day, today)) {
    return null
  }

  const currentTimePosition = getCurrentTimePosition(currentTime)

  return (
    <div
      className="pointer-events-none absolute right-0 left-0 z-20 flex items-center"
      style={{
        top: `${currentTimePosition}px`,
        transform: "translateY(-50%)",
      }}
    >
      <div className="-ml-1 size-2 shrink-0 rounded-full bg-red-500" />
      <div className="h-0.5 flex-1 bg-red-500" />
    </div>
  )
}

"use client"

import { ArrowUpRight01Icon } from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { Avatar, AvatarImage } from "@loveui/ui/ui/avatar"

import { Event } from "../../mock-data/events"
import { getEventDuration } from "./calendar-utils"

interface EventCardProps {
  event: Event
  style: any
  onClick?: () => void
}

export function EventCard({ event, style, onClick }: EventCardProps) {
  const duration = getEventDuration(event.startTime, event.endTime)
  const isVeryShortEvent = duration < 30
  const isMediumEvent = duration >= 25 && duration < 60
  const timeStr = `${event.startTime} - ${event.endTime}${
    event.timezone ? ` (${event.timezone})` : ""
  }`
  const hasMultipleParticipants = event.participants.length > 3

  if (isVeryShortEvent) {
    return (
      <div
        className="absolute right-2 left-2 z-10 flex cursor-pointer items-center gap-1.5 rounded-lg border border-border bg-card px-2 py-1 transition-colors hover:bg-muted"
        style={style}
        onClick={onClick}
      >
        <div className="size-1.5 shrink-0 rounded-full bg-cyan-500" />
        <h4 className="flex-1 truncate text-[10px] font-semibold text-foreground">
          {event.title}
        </h4>
        <span className="shrink-0 text-[9px] text-muted-foreground">
          {event.startTime}
        </span>
      </div>
    )
  }

  if (isMediumEvent) {
    return (
      <div
        className="absolute right-2 left-2 z-10 cursor-pointer rounded-lg border border-border bg-card px-2.5 py-2 transition-colors hover:bg-muted"
        style={style}
        onClick={onClick}
      >
        <div className="flex h-full flex-col gap-1">
          <div className="flex items-center gap-1.5">
            <div className="size-1.5 shrink-0 rounded-full bg-cyan-500" />
            <h4 className="flex-1 truncate text-[10px] font-semibold text-foreground">
              {event.title}
            </h4>
          </div>
          <p className="text-[9px] tracking-wide text-muted-foreground uppercase">
            {timeStr}
          </p>
        </div>
      </div>
    )
  }

  return (
    <div
      className="absolute right-2 left-2 z-10 cursor-pointer rounded-lg border border-border bg-card p-3 transition-colors hover:bg-muted"
      style={style}
      onClick={onClick}
    >
      <div className="flex h-full flex-col gap-1">
        <div className="min-h-0 flex-1">
          <h4
            className={`mb-1 text-xs font-semibold text-foreground ${
              duration <= 60 ? "truncate whitespace-nowrap" : "line-clamp-2"
            }`}
          >
            {event.title}
          </h4>
          <p className="mb-2 text-[10px] tracking-wide text-muted-foreground uppercase">
            {timeStr}
          </p>

          {event.participants.length > 0 && (
            <div className="mb-2 flex items-center gap-1.5">
              <div className="flex -space-x-1.5">
                {event.participants.slice(0, 3).map((participant, idx) => (
                  <Avatar
                    key={idx}
                    className="size-5 border-2 border-background"
                  >
                    <AvatarImage
                      src={`https://api.dicebear.com/9.x/glass/svg?seed=${participant}`}
                    />
                  </Avatar>
                ))}
              </div>
              {hasMultipleParticipants && (
                <span className="text-[10px] text-muted-foreground">
                  +{event.participants.length - 3}
                </span>
              )}
            </div>
          )}
        </div>

        {event.meetingLink && (
          <div className="mt-auto flex items-center gap-1.5 text-[10px] text-cyan-500">
            <div className="flex size-4 shrink-0 items-center justify-center rounded bg-cyan-500/10">
              <svg className="size-2.5" viewBox="0 0 24 24" fill="none">
                <path
                  d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 14.5v-9l6 4.5-6 4.5z"
                  fill="currentColor"
                />
              </svg>
            </div>
            <span className="flex-1 truncate">Join on Google Meet</span>
            <HugeiconsIcon
              icon={ArrowUpRight01Icon}
              className="size-3 shrink-0"
            />
          </div>
        )}
      </div>
    </div>
  )
}

"use client"

import * as React from "react"
import { ThemeProvider as NextThemesProvider } from "next-themes"

export function ThemeProvider({
  children,
  ...props
}: React.ComponentProps<typeof NextThemesProvider>) {
  return <NextThemesProvider {...props}>{children}</NextThemesProvider>
}

"use client"

import * as React from "react"
import { Moon01Icon, Sun01Icon } from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"
import { useTheme } from "next-themes"

import { Button } from "@loveui/ui/ui/button"

export function ThemeToggle() {
  const { setTheme, theme } = useTheme()

  return (
    <Button
      variant="ghost"
      size="icon"
      onClick={() => setTheme(theme === "light" ? "dark" : "light")}
    >
      <HugeiconsIcon
        icon={Sun01Icon}
        className="size-5 scale-100 rotate-0 transition-all dark:scale-0 dark:-rotate-90"
      />
      <HugeiconsIcon
        icon={Moon01Icon}
        className="absolute size-5 scale-0 rotate-90 transition-all dark:scale-100 dark:rotate-0"
      />
      <span className="sr-only">Toggle theme</span>
    </Button>
  )
}

"use client"

import * as React from "react"
import type { ComponentPropsWithoutRef } from "react"
import { AlertDialog as AlertDialogPrimitive } from "@base-ui/react/alert-dialog"

import { cn } from "../../lib/utils"
import { Button } from "./button"

function AlertDialog({ ...props }: AlertDialogPrimitive.Root.Props) {
  return <AlertDialogPrimitive.Root data-slot="alert-dialog" {...props} />
}

function AlertDialogTrigger({ ...props }: AlertDialogPrimitive.Trigger.Props) {
  return (
    <AlertDialogPrimitive.Trigger data-slot="alert-dialog-trigger" {...props} />
  )
}

function AlertDialogPortal({ ...props }: AlertDialogPrimitive.Portal.Props) {
  return (
    <AlertDialogPrimitive.Portal data-slot="alert-dialog-portal" {...props} />
  )
}

function AlertDialogOverlay({
  className,
  ...props
}: AlertDialogPrimitive.Backdrop.Props) {
  return (
    <AlertDialogPrimitive.Backdrop
      data-slot="alert-dialog-overlay"
      className={cn(
        "data-open:animate-in data-closed:animate-out data-closed:fade-out-0 data-open:fade-in-0 fixed inset-0 isolate z-50 bg-black/10 duration-100 supports-backdrop-filter:backdrop-blur-xs",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogContent({
  className,
  size = "default",
  ...props
}: AlertDialogPrimitive.Popup.Props & {
  size?: "default" | "sm"
}) {
  return (
    <AlertDialogPortal>
      <AlertDialogOverlay />
      <AlertDialogPrimitive.Popup
        data-slot="alert-dialog-content"
        data-size={size}
        className={cn(
          "data-open:animate-in data-closed:animate-out data-closed:fade-out-0 data-open:fade-in-0 data-closed:zoom-out-95 data-open:zoom-in-95 group/alert-dialog-content fixed top-1/2 left-1/2 z-50 grid w-full -translate-x-1/2 -translate-y-1/2 gap-6 rounded-xl bg-background p-6 ring-1 ring-foreground/10 duration-100 outline-none data-[size=default]:max-w-xs data-[size=sm]:max-w-xs data-[size=default]:sm:max-w-lg",
          className
        )}
        {...props}
      />
    </AlertDialogPortal>
  )
}

function AlertDialogHeader({
  className,
  ...props
}: ComponentPropsWithoutRef<"div">) {
  return (
    <div
      data-slot="alert-dialog-header"
      className={cn(
        "grid grid-rows-[auto_1fr] place-items-center gap-1.5 text-center has-data-[slot=alert-dialog-media]:grid-rows-[auto_auto_1fr] has-data-[slot=alert-dialog-media]:gap-x-6 sm:group-data-[size=default]/alert-dialog-content:place-items-start sm:group-data-[size=default]/alert-dialog-content:text-left sm:group-data-[size=default]/alert-dialog-content:has-data-[slot=alert-dialog-media]:grid-rows-[auto_1fr]",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogFooter({
  className,
  ...props
}: ComponentPropsWithoutRef<"div">) {
  return (
    <div
      data-slot="alert-dialog-footer"
      className={cn(
        "flex flex-col-reverse gap-2 group-data-[size=sm]/alert-dialog-content:grid group-data-[size=sm]/alert-dialog-content:grid-cols-2 sm:flex-row sm:justify-end",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogMedia({
  className,
  ...props
}: ComponentPropsWithoutRef<"div">) {
  return (
    <div
      data-slot="alert-dialog-media"
      className={cn(
        "mb-2 inline-flex size-16 items-center justify-center rounded-md bg-muted sm:group-data-[size=default]/alert-dialog-content:row-span-2 *:[svg:not([class*='size-'])]:size-8",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogTitle({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Title>) {
  return (
    <AlertDialogPrimitive.Title
      data-slot="alert-dialog-title"
      className={cn(
        "text-lg font-medium sm:group-data-[size=default]/alert-dialog-content:group-has-data-[slot=alert-dialog-media]/alert-dialog-content:col-start-2",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogDescription({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Description>) {
  return (
    <AlertDialogPrimitive.Description
      data-slot="alert-dialog-description"
      className={cn(
        "text-sm text-balance text-muted-foreground md:text-pretty *:[a]:underline *:[a]:underline-offset-3 *:[a]:hover:text-foreground",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogAction({
  className,
  ...props
}: React.ComponentProps<typeof Button>) {
  return (
    <Button
      data-slot="alert-dialog-action"
      className={cn(className)}
      {...props}
    />
  )
}

function AlertDialogCancel({
  className,
  variant = "outline",
  size = "default",
  ...props
}: AlertDialogPrimitive.Close.Props &
  Pick<React.ComponentProps<typeof Button>, "variant" | "size">) {
  return (
    <AlertDialogPrimitive.Close
      data-slot="alert-dialog-cancel"
      className={cn(className)}
      render={<Button variant={variant} size={size} />}
      {...props}
    />
  )
}

export {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogMedia,
  AlertDialogOverlay,
  AlertDialogPortal,
  AlertDialogTitle,
  AlertDialogTrigger,
}

