"use client"

import * as React from "react"
import {
  Circle,
  Layers,
  Map,
  Mountain,
  Navigation,
  Satellite,
  ZoomIn,
  ZoomOut,
} from "lucide-react"

import { Button } from "@loveui/ui/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"
import { Tooltip, TooltipContent, TooltipTrigger } from "@loveui/ui/ui/tooltip"

import { cn } from "../../lib/utils"
import { useRentalsStore } from "../../store/rentals-store"
import { ThemeToggle } from "../theme-toggle"

const mapStyles = [
  {
    id: "default",
    name: "Default",
    icon: Circle,
    description: "Follows theme",
  },
  { id: "streets", name: "Streets", icon: Map, description: "Detailed roads" },
  {
    id: "outdoors",
    name: "Outdoors",
    icon: Mountain,
    description: "Terrain & trails",
  },
  {
    id: "satellite",
    name: "Satellite",
    icon: Satellite,
    description: "Aerial view",
  },
] as const

export function MapControls() {
  const {
    mapZoom,
    setMapZoom,
    setMapCenter,
    setUserLocation,
    mapStyle,
    setMapStyle,
  } = useRentalsStore()

  const handleZoomIn = () => {
    setMapZoom(Math.min(mapZoom + 1, 18))
  }

  const handleZoomOut = () => {
    setMapZoom(Math.max(mapZoom - 1, 3))
  }

  const handleLocate = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          }
          setUserLocation(location)
          setMapCenter(location)
          setMapZoom(15)
        },
        () => {
          alert("Unable to get your location. Please try again later.")
        },
        { enableHighAccuracy: false, timeout: 5000, maximumAge: 300000 }
      )
    } else {
      alert("Geolocation is not supported by your browser.")
    }
  }

  return (
    <div className="absolute top-4 right-4 z-20 flex flex-col gap-2">
      <div className="flex flex-col gap-2 rounded-lg border bg-background p-1 shadow-lg">
        <DropdownMenu>
          <Tooltip>
            <TooltipTrigger
              render={
                <DropdownMenuTrigger
                  render={
                    <Button variant="ghost" size="icon" className="h-9 w-9">
                      <Layers className="h-4 w-4" />
                    </Button>
                  }
                />
              }
            />
            <TooltipContent>Map style</TooltipContent>
          </Tooltip>
          <DropdownMenuContent align="end" className="w-48">
            {mapStyles.map((style) => {
              const Icon = style.icon
              return (
                <DropdownMenuItem
                  key={style.id}
                  onClick={() =>
                    setMapStyle(
                      style.id as
                        | "default"
                        | "streets"
                        | "outdoors"
                        | "satellite"
                    )
                  }
                  className={cn("gap-3", mapStyle === style.id && "bg-accent")}
                >
                  <Icon className="size-4 shrink-0" />
                  <div className="flex flex-col">
                    <span className="font-medium">{style.name}</span>
                    <span className="text-xs text-muted-foreground">
                      {style.description}
                    </span>
                  </div>
                </DropdownMenuItem>
              )
            })}
          </DropdownMenuContent>
        </DropdownMenu>
        <Tooltip>
          <TooltipTrigger
            render={
              <Button
                variant="ghost"
                size="icon"
                onClick={handleZoomIn}
                className="h-9 w-9"
              >
                <ZoomIn className="h-4 w-4" />
              </Button>
            }
          />
          <TooltipContent>Zoom in</TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger
            render={
              <Button
                variant="ghost"
                size="icon"
                onClick={handleZoomOut}
                className="h-9 w-9"
              >
                <ZoomOut className="h-4 w-4" />
              </Button>
            }
          />
          <TooltipContent>Zoom out</TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger
            render={
              <Button
                variant="ghost"
                size="icon"
                onClick={handleLocate}
                className="h-9 w-9"
              >
                <Navigation className="h-4 w-4" />
              </Button>
            }
          />
          <TooltipContent>Locate me</TooltipContent>
        </Tooltip>
      </div>

      <div className="flex flex-col gap-2 rounded-lg border bg-background p-1 shadow-lg">
        <ThemeToggle />
      </div>
    </div>
  )
}

"use client"

import * as React from "react"
import maplibregl from "maplibre-gl"

import "maplibre-gl/dist/maplibre-gl.css"

import { useTheme } from "next-themes"

import { propertyTypeLabels } from "../../mock-data/listings"
import { useRentalsStore } from "../../store/rentals-store"

const MAP_STYLES = {
  light: "https://basemaps.cartocdn.com/gl/positron-gl-style/style.json",
  dark: "https://basemaps.cartocdn.com/gl/dark-matter-gl-style/style.json",
  streets: "https://basemaps.cartocdn.com/gl/voyager-gl-style/style.json",
  outdoors: "https://tiles.stadiamaps.com/styles/outdoors.json",
  satellite: "https://tiles.stadiamaps.com/styles/alidade_satellite.json",
}

export function MapView() {
  const containerRef = React.useRef<HTMLDivElement>(null)
  const mapRef = React.useRef<maplibregl.Map | null>(null)
  const markersRef = React.useRef<Map<string, maplibregl.Marker>>(new Map())
  const popupRef = React.useRef<maplibregl.Popup | null>(null)
  const isAnimatingRef = React.useRef(false)
  const { resolvedTheme } = useTheme()

  const {
    mapCenter,
    mapZoom,
    mapStyle,
    setMapCenter,
    setMapZoom,
    selectListing,
    selectedListingId,
    getFilteredListings,
  } = useRentalsStore()

  const listings = getFilteredListings()

  const getMapStyleUrl = React.useCallback(() => {
    if (mapStyle === "default") {
      return resolvedTheme === "dark" ? MAP_STYLES.dark : MAP_STYLES.light
    }
    return MAP_STYLES[mapStyle]
  }, [mapStyle, resolvedTheme])

  React.useEffect(() => {
    if (!containerRef.current || mapRef.current) return

    const map = new maplibregl.Map({
      container: containerRef.current,
      style: getMapStyleUrl(),
      center: [mapCenter.lng, mapCenter.lat],
      zoom: mapZoom,
      minZoom: 3,
      maxZoom: 18,
      attributionControl: false,
    })

    map.addControl(
      new maplibregl.AttributionControl({ compact: true }),
      "bottom-right"
    )

    map.on("moveend", () => {
      if (isAnimatingRef.current) {
        isAnimatingRef.current = false
        return
      }
      const center = map.getCenter()
      const zoom = map.getZoom()
      setMapCenter({ lat: center.lat, lng: center.lng })
      setMapZoom(zoom)
    })

    mapRef.current = map

    const handleMapClick = (e: maplibregl.MapMouseEvent) => {
      if (popupRef.current) {
        const popupElement = popupRef.current.getElement()
        if (
          popupElement &&
          !popupElement.contains(e.originalEvent.target as Node)
        ) {
          const target = e.originalEvent.target as HTMLElement
          if (!target.closest(".listing-marker-container")) {
            popupRef.current.remove()
            popupRef.current = null
            selectListing(null)
          }
        }
      }
    }

    map.on("click", handleMapClick)

    return () => {
      map.off("click", handleMapClick)
      map.remove()
      mapRef.current = null
    }
  }, [selectListing])

  React.useEffect(() => {
    if (!mapRef.current) return
    mapRef.current.setStyle(getMapStyleUrl())
  }, [mapStyle, resolvedTheme, getMapStyleUrl])

  React.useEffect(() => {
    if (!mapRef.current) return

    markersRef.current.forEach((marker) => marker.remove())
    markersRef.current.clear()

    listings.forEach((listing) => {
      const isSelected = selectedListingId === listing.id

      const el = document.createElement("div")
      el.className = "listing-marker-container"
      const priceText = `$${listing.pricePerNight}`
      el.innerHTML = `
        <div class="relative cursor-pointer transition-all ${
          isSelected ? "scale-110 z-10" : "hover:scale-105"
        }">
          <div class="px-2.5 py-1.5 rounded-lg bg-card border-2 ${
            isSelected
              ? "border-primary shadow-xl"
              : "border-border shadow-lg hover:shadow-xl"
          } flex items-center justify-center min-w-[60px]">
            <span class="text-sm font-semibold text-card-foreground whitespace-nowrap">${priceText}</span>
          </div>
          ${
            isSelected
              ? '<div class="absolute -bottom-1 left-1/2 -translate-x-1/2 w-2 h-2 rounded-full bg-primary animate-ping"></div>'
              : ""
          }
        </div>
      `

      el.addEventListener("click", (e) => {
        e.stopPropagation()

        if (popupRef.current) {
          const currentPopup = popupRef.current
          const currentLngLat = currentPopup.getLngLat()
          if (
            currentLngLat.lng === listing.coordinates.lng &&
            currentLngLat.lat === listing.coordinates.lat
          ) {
            currentPopup.remove()
            popupRef.current = null
            selectListing(null)
            return
          }
          currentPopup.remove()
        }

        if (!mapRef.current) return

        const popupContent = `
          <div class="listing-popup-content">
            <div class="relative w-full h-64 mb-4 rounded-t-lg overflow-hidden">
              <img 
                src="${listing.images[0]}" 
                alt="${listing.title}"
                class="w-full h-full object-cover"
                onerror="this.src='https://images.unsplash.com/photo-1516035069371-29a1b244cc32?w=600&h=400&fit=crop'"
              />
              <button 
                class="close-popup-btn absolute top-3 right-3 w-8 h-8 rounded-full bg-white/95 backdrop-blur-sm hover:bg-white flex items-center justify-center transition-all shadow-md hover:shadow-lg z-10"
                aria-label="Close"
              >
                <svg class="w-4 h-4 text-gray-700" fill="none" stroke="currentColor" stroke-width="2.5" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div class="px-4 pb-4">
              <div class="flex items-start justify-between mb-2">
                <div class="flex-1 min-w-0">
                  <h3 class="font-semibold text-base mb-1">${
                    propertyTypeLabels[listing.propertyType]
                  } • ${listing.city}</h3>
                  <p class="text-sm text-muted-foreground mb-2 line-clamp-2">${
                    listing.title
                  }</p>
                </div>
                <div class="flex items-center gap-1 ml-2 shrink-0">
                  <svg class="w-4 h-4 fill-yellow-400 text-yellow-400" viewBox="0 0 20 20">
                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                  </svg>
                  <span class="text-sm font-medium">${listing.rating.toFixed(
                    1
                  )}</span>
                  <span class="text-xs text-muted-foreground">(${
                    listing.reviewCount
                  })</span>
                </div>
              </div>
              <div class="flex items-center gap-2 text-sm text-muted-foreground mb-3">
                ${
                  listing.bedrooms > 0
                    ? `<span>${listing.bedrooms} kit${
                        listing.bedrooms > 1 ? "s" : ""
                      }</span>`
                    : ""
                }
                ${
                  listing.bedrooms > 0 && listing.bathrooms > 0
                    ? "<span>•</span>"
                    : ""
                }
                ${
                  listing.bathrooms > 0
                    ? `<span>${listing.bathrooms} add-on${
                        listing.bathrooms > 1 ? "s" : ""
                      }</span>`
                    : ""
                }
              </div>
              <div class="flex items-baseline gap-2">
                <span class="text-xl font-semibold">$${
                  listing.pricePerNight
                }</span>
                <span class="text-sm text-muted-foreground">/ day</span>
              </div>
            </div>
          </div>
        `

        const point = mapRef.current.project([
          listing.coordinates.lng,
          listing.coordinates.lat,
        ])
        const mapHeight = mapRef.current.getContainer().clientHeight
        const mapWidth = mapRef.current.getContainer().clientWidth

        const isNearBottom = point.y > mapHeight * 0.7
        const isNearTop = point.y < mapHeight * 0.3
        const isNearRight = point.x > mapWidth * 0.7
        const isNearLeft = point.x < mapWidth * 0.3

        let offsetX = 0
        let offsetY = -30

        if (isNearBottom) {
          offsetY = 20
        } else if (isNearTop) {
          offsetY = -60
        }

        if (isNearRight) {
          offsetX = -20
        } else if (isNearLeft) {
          offsetX = 20
        }

        const popup = new maplibregl.Popup({
          offset: [offsetX, offsetY],
          closeButton: false,
          closeOnClick: false,
          className: "listing-popup",
          maxWidth: "400px",
        })
          .setLngLat([listing.coordinates.lng, listing.coordinates.lat])
          .setHTML(popupContent)
          .addTo(mapRef.current!)

        const popupElement = popup.getElement()
        if (popupElement) {
          popupElement.style.zIndex = "1000"

          const closeButton = popupElement.querySelector(".close-popup-btn")
          if (closeButton) {
            closeButton.addEventListener("click", (e) => {
              e.stopPropagation()
              popup.remove()
              popupRef.current = null
              selectListing(null)
            })
          }

          popupElement.addEventListener("click", (e) => {
            e.stopPropagation()
            if (mapRef.current) {
              isAnimatingRef.current = true
              mapRef.current.flyTo({
                center: [listing.coordinates.lng, listing.coordinates.lat],
                zoom: Math.max(mapRef.current.getZoom(), 14),
                essential: true,
              })
            }
          })
        }

        popupRef.current = popup
        selectListing(listing.id)
      })

      const marker = new maplibregl.Marker({ element: el })
        .setLngLat([listing.coordinates.lng, listing.coordinates.lat])
        .addTo(mapRef.current!)

      markersRef.current.set(listing.id, marker)
    })
  }, [listings, selectedListingId, selectListing])

  const lastCenterRef = React.useRef({
    lat: mapCenter.lat,
    lng: mapCenter.lng,
  })
  const lastZoomRef = React.useRef(mapZoom)

  React.useEffect(() => {
    if (!mapRef.current) return

    const centerChanged =
      Math.abs(lastCenterRef.current.lat - mapCenter.lat) > 0.0001 ||
      Math.abs(lastCenterRef.current.lng - mapCenter.lng) > 0.0001
    const zoomChanged = Math.abs(lastZoomRef.current - mapZoom) > 0.1

    if (centerChanged || zoomChanged) {
      isAnimatingRef.current = true
      mapRef.current.flyTo({
        center: [mapCenter.lng, mapCenter.lat],
        zoom: mapZoom,
        essential: true,
      })
      lastCenterRef.current = { lat: mapCenter.lat, lng: mapCenter.lng }
      lastZoomRef.current = mapZoom
    }
  }, [mapCenter, mapZoom])

  return <div ref={containerRef} className="absolute inset-0 h-full w-full" />
}

"use client"

import * as React from "react"
import { ThemeProvider as NextThemesProvider } from "next-themes"

export function ThemeProvider({
  children,
  ...props
}: React.ComponentProps<typeof NextThemesProvider>) {
  return <NextThemesProvider {...props}>{children}</NextThemesProvider>
}

"use client"

import * as React from "react"
import { Moon, Sun } from "lucide-react"
import { useTheme } from "next-themes"

import { Button } from "@loveui/ui/ui/button"

interface ThemeToggleProps {
  className?: string
}

export function ThemeToggle({ className }: ThemeToggleProps) {
  const { setTheme, theme } = useTheme()

  return (
    <Button
      variant="outline"
      size="icon"
      className={className}
      onClick={() => setTheme(theme === "light" ? "dark" : "light")}
    >
      <Sun className="size-5 scale-100 rotate-0 transition-all dark:scale-0 dark:-rotate-90" />
      <Moon className="absolute size-5 scale-0 rotate-90 transition-all dark:scale-100 dark:rotate-0" />
      <span className="sr-only">Toggle theme</span>
    </Button>
  )
}

import { mergeProps } from "@base-ui/react/merge-props"
import { useRender } from "@base-ui/react/use-render"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "../../lib/utils"

const badgeVariants = cva(
  "group/badge inline-flex h-5 w-fit shrink-0 items-center justify-center gap-1 overflow-hidden rounded-4xl border border-transparent px-2 py-0.5 text-xs font-medium whitespace-nowrap transition-all transition-colors focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 aria-invalid:border-destructive aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 [&>svg]:pointer-events-none [&>svg]:size-3!",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground [a]:hover:bg-primary/80",
        secondary:
          "bg-secondary text-secondary-foreground [a]:hover:bg-secondary/80",
        destructive:
          "bg-destructive/10 text-destructive focus-visible:ring-destructive/20 dark:bg-destructive/20 dark:focus-visible:ring-destructive/40 [a]:hover:bg-destructive/20",
        outline:
          "border-border text-foreground [a]:hover:bg-muted [a]:hover:text-muted-foreground",
        ghost:
          "hover:bg-muted hover:text-muted-foreground dark:hover:bg-muted/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

function Badge({
  className,
  variant = "default",
  render,
  ...props
}: useRender.ComponentProps<"span"> & VariantProps<typeof badgeVariants>) {
  return useRender({
    defaultTagName: "span",
    props: mergeProps<"span">(
      {
        className: cn(badgeVariants({ className, variant })),
      },
      props
    ),
    render,
    state: {
      slot: "badge",
      variant,
    },
  })
}

export { Badge, badgeVariants }

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "../../lib/utils"

const buttonVariants = cva(
  "group/button inline-flex shrink-0 cursor-pointer items-center justify-center rounded-md border border-transparent bg-clip-padding text-sm font-medium whitespace-nowrap transition-all outline-none select-none focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 disabled:pointer-events-none disabled:opacity-50 aria-invalid:border-destructive aria-invalid:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:border-destructive/50 dark:aria-invalid:ring-destructive/40 [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/80",
        outline:
          "border-border bg-background shadow-xs hover:bg-muted hover:text-foreground aria-expanded:bg-muted aria-expanded:text-foreground dark:border-input dark:bg-input/30 dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80 aria-expanded:bg-secondary aria-expanded:text-secondary-foreground",
        ghost:
          "hover:bg-muted hover:text-foreground aria-expanded:bg-muted aria-expanded:text-foreground dark:hover:bg-muted/50",
        destructive:
          "bg-destructive/10 text-destructive hover:bg-destructive/20 focus-visible:border-destructive/40 focus-visible:ring-destructive/20 dark:bg-destructive/20 dark:hover:bg-destructive/30 dark:focus-visible:ring-destructive/40",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default:
          "h-9 gap-1.5 px-2.5 in-data-[slot=button-group]:rounded-md has-data-[icon=inline-end]:pr-2 has-data-[icon=inline-start]:pl-2",
        xs: "h-6 gap-1 rounded-[min(var(--radius-md),8px)] px-2 text-xs in-data-[slot=button-group]:rounded-md has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 [&_svg:not([class*='size-'])]:size-3",
        sm: "h-8 gap-1 rounded-[min(var(--radius-md),10px)] px-2.5 in-data-[slot=button-group]:rounded-md has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5",
        lg: "h-10 gap-1.5 px-2.5 has-data-[icon=inline-end]:pr-3 has-data-[icon=inline-start]:pl-3",
        icon: "size-9",
        "icon-xs":
          "size-6 rounded-[min(var(--radius-md),8px)] in-data-[slot=button-group]:rounded-md [&_svg:not([class*='size-'])]:size-3",
        "icon-sm":
          "size-8 rounded-[min(var(--radius-md),10px)] in-data-[slot=button-group]:rounded-md",
        "icon-lg": "size-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

function Button({
  className,
  variant = "default",
  size = "default",
  asChild = false,
  ...props
}: ButtonProps) {
  const Comp: any = asChild ? Slot : "button"
  return (
    <Comp
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...(props as any)}
    />
  )
}

export { Button, buttonVariants }

"use client"

import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "../../lib/utils"
import { Button } from "./button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

function Carousel({
  orientation = "horizontal",
  opts,
  setApi,
  plugins,
  className,
  children,
  ...props
}: React.ComponentProps<"div"> & CarouselProps) {
  const [carouselRef, api] = useEmblaCarousel(
    {
      ...opts,
      axis: orientation === "horizontal" ? "x" : "y",
    },
    plugins
  )
  const [canScrollPrev, setCanScrollPrev] = React.useState(false)
  const [canScrollNext, setCanScrollNext] = React.useState(false)

  const onSelect = React.useCallback((api: CarouselApi) => {
    if (!api) return
    setCanScrollPrev(api.canScrollPrev())
    setCanScrollNext(api.canScrollNext())
  }, [])

  const scrollPrev = React.useCallback(() => {
    api?.scrollPrev()
  }, [api])

  const scrollNext = React.useCallback(() => {
    api?.scrollNext()
  }, [api])

  const handleKeyDown = React.useCallback(
    (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (event.key === "ArrowLeft") {
        event.preventDefault()
        scrollPrev()
      } else if (event.key === "ArrowRight") {
        event.preventDefault()
        scrollNext()
      }
    },
    [scrollPrev, scrollNext]
  )

  React.useEffect(() => {
    if (!api || !setApi) return
    setApi(api)
  }, [api, setApi])

  React.useEffect(() => {
    if (!api) return
    onSelect(api)
    api.on("reInit", onSelect)
    api.on("select", onSelect)

    return () => {
      api?.off("select", onSelect)
    }
  }, [api, onSelect])

  return (
    <CarouselContext.Provider
      value={{
        carouselRef,
        api: api,
        opts,
        orientation:
          orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
        scrollPrev,
        scrollNext,
        canScrollPrev,
        canScrollNext,
      }}
    >
      <div
        onKeyDownCapture={handleKeyDown}
        className={cn("relative", className)}
        role="region"
        aria-roledescription="carousel"
        data-slot="carousel"
        {...props}
      >
        {children}
      </div>
    </CarouselContext.Provider>
  )
}

function CarouselContent({ className, ...props }: React.ComponentProps<"div">) {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div
      ref={carouselRef}
      className="overflow-hidden"
      data-slot="carousel-content"
    >
      <div
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
}

function CarouselItem({ className, ...props }: React.ComponentProps<"div">) {
  const { orientation } = useCarousel()

  return (
    <div
      role="group"
      aria-roledescription="slide"
      data-slot="carousel-item"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
}

function CarouselPrevious({
  className,
  variant = "outline",
  size = "icon",
  ...props
}: React.ComponentProps<typeof Button>) {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <>
      {canScrollPrev && (
        <Button
          data-slot="carousel-previous"
          variant={variant}
          size={size}
          className={cn(
            "absolute size-8 rounded-full",
            orientation === "horizontal"
              ? "top-1/2 -left-12 -translate-y-1/2"
              : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
            className
          )}
          onClick={(e) => {
            e.stopPropagation()
            scrollPrev()
          }}
          {...props}
        >
          <ArrowLeft className="size-4" />
          <span className="sr-only">Previous slide</span>
        </Button>
      )}
    </>
  )
}

function CarouselNext({
  className,
  variant = "outline",
  size = "icon",
  ...props
}: React.ComponentProps<typeof Button>) {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <>
      {canScrollNext && (
        <Button
          data-slot="carousel-next"
          variant={variant}
          size={size}
          className={cn(
            "absolute size-8 rounded-full",
            orientation === "horizontal"
              ? "top-1/2 -right-12 -translate-y-1/2"
              : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
            className
          )}
          onClick={(e) => {
            e.stopPropagation()
            scrollNext()
          }}
          {...props}
        >
          <ArrowRight className="size-4" />
          <span className="sr-only">Next slide</span>
        </Button>
      )}
    </>
  )
}

