"use client"

import { useMemo, useState } from "react"
import {
  ArrowDown01Icon,
  Calendar01Icon,
  Settings01Icon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"
import { useTheme } from "next-themes"
import {
  Area,
  AreaChart,
  CartesianGrid,
  Line,
  LineChart as RechartsLineChart,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from "recharts"

import { Button } from "@loveui/ui/ui/button"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"

import {
  leadsChartDataMonth,
  leadsChartDataQuarter,
  leadsChartDataWeek,
} from "../../mock-data/dashboard"

type ChartType = "line" | "area"
type Period = "last_week" | "last_month" | "last_quarter"

const periodLabels: Record<Period, string> = {
  last_week: "Last Week",
  last_month: "Last Month",
  last_quarter: "Last Quarter",
}

const lineColors = {
  line1: "#ec4899",
  line2: "#06b6d4",
  line3: "#f97316",
  line4: "#22c55e",
}

interface CustomTooltipProps {
  active?: boolean
  payload?: Array<{
    value: number
    dataKey: string
    color: string
  }>
  label?: string
}

function CustomTooltip({ active, payload, label }: CustomTooltipProps) {
  if (active && payload && payload.length) {
    return (
      <div className="rounded-md border bg-card p-3 shadow-lg">
        <p className="mb-2 text-xs text-muted-foreground">{label}</p>
        <div className="grid grid-cols-2 gap-x-4 gap-y-1">
          {payload.map((entry, index) => (
            <div key={index} className="flex items-center gap-1.5">
              <span
                className="size-2 rounded-full"
                style={{ backgroundColor: entry.color }}
              />
              <span className="text-xs font-medium">{entry.value}</span>
            </div>
          ))}
        </div>
      </div>
    )
  }
  return null
}

export function LeadsChart() {
  const { theme } = useTheme()
  const [chartType, setChartType] = useState<ChartType>("line")
  const [period, setPeriod] = useState<Period>("last_month")
  const [showGrid, setShowGrid] = useState(true)
  const [visibleLines, setVisibleLines] = useState({
    line1: true,
    line2: true,
    line3: true,
    line4: true,
  })

  const axisColor = theme === "dark" ? "#71717a" : "#868c98"
  const gridColor = theme === "dark" ? "#3f3f46" : "#e2e4e9"

  const chartData = useMemo(() => {
    switch (period) {
      case "last_week":
        return leadsChartDataWeek
      case "last_month":
        return leadsChartDataMonth
      case "last_quarter":
        return leadsChartDataQuarter
      default:
        return leadsChartDataMonth
    }
  }, [period])

  const toggleLine = (line: keyof typeof visibleLines) => {
    setVisibleLines((prev) => ({
      ...prev,
      [line]: !prev[line],
    }))
  }

  const resetToDefault = () => {
    setChartType("line")
    setPeriod("last_month")
    setShowGrid(true)
    setVisibleLines({
      line1: true,
      line2: true,
      line3: true,
      line4: true,
    })
  }

  return (
    <div className="flex-1 rounded-lg border bg-card text-card-foreground">
      <div className="flex flex-col gap-3 border-b border-border/50 p-4 sm:flex-row sm:items-center sm:justify-between">
        <h3 className="text-sm font-medium sm:text-base">
          Buyer Inquiry Trend
        </h3>
        <div className="flex items-center gap-2">
          <DropdownMenu>
            <DropdownMenuTrigger
              render={
                <Button variant="outline" size="sm" className="h-7 gap-1.5">
                  <HugeiconsIcon icon={Calendar01Icon} className="size-3.5" />
                  <span className="text-sm">{periodLabels[period]}</span>
                  <HugeiconsIcon icon={ArrowDown01Icon} className="size-3" />
                </Button>
              }
            />
            <DropdownMenuContent align="end">
              <DropdownMenuGroup>
                {(Object.keys(periodLabels) as Period[]).map((p) => (
                  <DropdownMenuItem key={p} onClick={() => setPeriod(p)}>
                    {periodLabels[p]} {period === p && "✓"}
                  </DropdownMenuItem>
                ))}
              </DropdownMenuGroup>
            </DropdownMenuContent>
          </DropdownMenu>

          <DropdownMenu>
            <DropdownMenuTrigger
              render={
                <Button variant="outline" size="icon" className="size-7">
                  <HugeiconsIcon icon={Settings01Icon} className="size-3.5" />
                </Button>
              }
            />
            <DropdownMenuContent align="end" className="w-48">
              <DropdownMenuSub>
                <DropdownMenuSubTrigger>Chart Type</DropdownMenuSubTrigger>
                <DropdownMenuSubContent>
                  <DropdownMenuGroup>
                    <DropdownMenuItem onClick={() => setChartType("line")}>
                      Line Chart {chartType === "line" && "✓"}
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => setChartType("area")}>
                      Area Chart {chartType === "area" && "✓"}
                    </DropdownMenuItem>
                  </DropdownMenuGroup>
                </DropdownMenuSubContent>
              </DropdownMenuSub>
              <DropdownMenuSeparator />
              <DropdownMenuCheckboxItem
                checked={showGrid}
                onCheckedChange={setShowGrid}
              >
                Show Grid
              </DropdownMenuCheckboxItem>
              <DropdownMenuSeparator />
              <DropdownMenuSub>
                <DropdownMenuSubTrigger>Show Lines</DropdownMenuSubTrigger>
                <DropdownMenuSubContent>
                  <DropdownMenuGroup>
                    {Object.entries(visibleLines).map(([key, value]) => (
                      <DropdownMenuCheckboxItem
                        key={key}
                        checked={value}
                        onCheckedChange={() =>
                          toggleLine(key as keyof typeof visibleLines)
                        }
                      >
                        <span
                          className="mr-2 size-2 rounded-full"
                          style={{
                            backgroundColor:
                              lineColors[key as keyof typeof lineColors],
                          }}
                        />
                        Series {key.replace("line", "")}
                      </DropdownMenuCheckboxItem>
                    ))}
                  </DropdownMenuGroup>
                </DropdownMenuSubContent>
              </DropdownMenuSub>
              <DropdownMenuSeparator />
              <DropdownMenuItem onClick={resetToDefault}>
                Reset to Default
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
      <div className="p-4">
        <div className="h-[200px] w-full sm:h-[250px]">
          <ResponsiveContainer width="100%" height="100%">
            {chartType === "area" ? (
              <AreaChart
                data={chartData}
                margin={{ top: 10, right: 10, left: -20, bottom: 0 }}
              >
                {showGrid && (
                  <CartesianGrid
                    strokeDasharray="3 3"
                    stroke={gridColor}
                    vertical={false}
                  />
                )}
                <XAxis
                  dataKey="date"
                  axisLine={false}
                  tickLine={false}
                  tick={{ fontSize: 10, fill: axisColor }}
                />
                <YAxis
                  axisLine={false}
                  tickLine={false}
                  tick={{ fontSize: 10, fill: axisColor }}
                  domain={[0, 1000]}
                />
                <Tooltip content={<CustomTooltip />} />
                <defs>
                  {Object.entries(lineColors).map(([key, color]) => (
                    <linearGradient
                      key={key}
                      id={`gradient-${key}`}
                      x1="0"
                      y1="0"
                      x2="0"
                      y2="1"
                    >
                      <stop offset="0%" stopColor={color} stopOpacity={0.3} />
                      <stop offset="100%" stopColor={color} stopOpacity={0} />
                    </linearGradient>
                  ))}
                </defs>
                {Object.entries(visibleLines).map(
                  ([key, visible]) =>
                    visible && (
                      <Area
                        key={key}
                        type="monotone"
                        dataKey={key}
                        stroke={lineColors[key as keyof typeof lineColors]}
                        strokeWidth={2}
                        fill={`url(#gradient-${key})`}
                        dot={false}
                      />
                    )
                )}
              </AreaChart>
            ) : (
              <RechartsLineChart
                data={chartData}
                margin={{ top: 10, right: 10, left: -20, bottom: 0 }}
              >
                {showGrid && (
                  <CartesianGrid
                    strokeDasharray="3 3"
                    stroke={gridColor}
                    vertical={false}
                  />
                )}
                <XAxis
                  dataKey="date"
                  axisLine={false}
                  tickLine={false}
                  tick={{ fontSize: 10, fill: axisColor }}
                />
                <YAxis
                  axisLine={false}
                  tickLine={false}
                  tick={{ fontSize: 10, fill: axisColor }}
                  domain={[0, 1000]}
                />
                <Tooltip content={<CustomTooltip />} />
                {Object.entries(visibleLines).map(
                  ([key, visible]) =>
                    visible && (
                      <Line
                        key={key}
                        type="monotone"
                        dataKey={key}
                        stroke={lineColors[key as keyof typeof lineColors]}
                        strokeWidth={2}
                        dot={false}
                        activeDot={{ r: 4, strokeWidth: 0 }}
                      />
                    )
                )}
              </RechartsLineChart>
            )}
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  )
}

"use client"

import { useMemo, useState } from "react"
import {
  Activity01Icon,
  ArrowDown01Icon,
  ArrowLeft01Icon,
  ArrowLeftDoubleIcon,
  ArrowRight01Icon,
  ArrowRightDoubleIcon,
  ArrowUp01Icon,
  ArrowUpDownIcon,
  CallIcon,
  Cancel01Icon,
  FireIcon,
  FlashIcon,
  Globe02Icon,
  HandPointingRight01Icon,
  Linkedin01Icon,
  Mail01Icon,
  PieChartIcon,
  Search01Icon,
  SlidersHorizontalIcon,
  SnowIcon,
  Target01Icon,
  Tick01Icon,
  Upload01Icon,
  UserIcon,
  UserMultiple02Icon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { Avatar, AvatarFallback, AvatarImage } from "@loveui/ui/ui/avatar"
import { Button } from "@loveui/ui/ui/button"
import { Checkbox } from "@loveui/ui/ui/checkbox"
import { Input } from "@loveui/ui/ui/input"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@loveui/ui/ui/menu"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@loveui/ui/ui/select"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@loveui/ui/ui/table"

import { cn } from "../../lib/utils"
import {
  leads,
  LeadSource,
  LeadStatus,
  LeadType,
} from "../../mock-data/dashboard"
import { useDashboardStore } from "../../store/dashboard-store"

type SortField = "name" | "email" | "followUp" | "status" | "score"
type SortOrder = "asc" | "desc"

function getSortIcon(
  sortField: SortField,
  sortOrder: SortOrder,
  field: SortField
) {
  if (sortField !== field)
    return <HugeiconsIcon icon={ArrowUpDownIcon} className="size-3" />
  return sortOrder === "asc" ? (
    <HugeiconsIcon icon={ArrowUp01Icon} className="size-3" />
  ) : (
    <HugeiconsIcon icon={ArrowDown01Icon} className="size-3" />
  )
}

function TypeBadge({ type }: { type: LeadType }) {
  if (type === "cold") {
    return (
      <div
        className="flex w-fit items-center gap-1 rounded-lg border border-cyan-500/40 px-2 py-1"
        style={{
          backgroundImage:
            "linear-gradient(90deg, rgba(6, 182, 212, 0.12) 0%, rgba(6, 182, 212, 0.06) 30%, rgba(6, 182, 212, 0) 100%), linear-gradient(90deg, hsl(var(--card)) 0%, hsl(var(--card)) 100%)",
        }}
      >
        <HugeiconsIcon icon={SnowIcon} className="size-3.5 text-cyan-400" />
        <span className="text-sm font-medium text-cyan-400">Cold</span>
      </div>
    )
  }

  return (
    <div
      className="flex w-fit items-center gap-1 rounded-lg border border-pink-500/40 px-2 py-1"
      style={{
        backgroundImage:
          "linear-gradient(90deg, rgba(236, 72, 153, 0.12) 0%, rgba(236, 72, 153, 0.06) 30%, rgba(236, 72, 153, 0) 100%), linear-gradient(90deg, hsl(var(--card)) 0%, hsl(var(--card)) 100%)",
      }}
    >
      <HugeiconsIcon icon={FireIcon} className="size-3.5 text-pink-400" />
      <span className="text-sm font-medium text-pink-400">Warm</span>
    </div>
  )
}

function StatusBadge({ status }: { status: LeadStatus }) {
  if (status === "closed") {
    return (
      <div
        className="flex w-fit items-center gap-1 rounded-lg border border-emerald-500/40 px-2 py-1"
        style={{
          backgroundImage:
            "linear-gradient(90deg, rgba(16, 185, 129, 0.12) 0%, rgba(16, 185, 129, 0.06) 30%, rgba(16, 185, 129, 0) 100%), linear-gradient(90deg, hsl(var(--card)) 0%, hsl(var(--card)) 100%)",
        }}
      >
        <HugeiconsIcon
          icon={Tick01Icon}
          className="size-3.5 text-emerald-400"
        />
        <span className="text-sm font-medium text-emerald-400">Closed</span>
      </div>
    )
  }

  return (
    <div
      className="flex w-fit items-center gap-1 rounded-lg border border-amber-500/40 px-2 py-1"
      style={{
        backgroundImage:
          "linear-gradient(90deg, rgba(245, 158, 11, 0.12) 0%, rgba(245, 158, 11, 0.06) 30%, rgba(245, 158, 11, 0) 100%), linear-gradient(90deg, hsl(var(--card)) 0%, hsl(var(--card)) 100%)",
      }}
    >
      <HugeiconsIcon icon={Cancel01Icon} className="size-3.5 text-amber-400" />
      <span className="text-sm font-medium text-amber-400">Lost</span>
    </div>
  )
}

function ScoreBadge({ score }: { score: number }) {
  const getScoreStyle = () => {
    if (score >= 80)
      return { barClass: "bg-emerald-500", textClass: "text-emerald-400" }
    if (score >= 50)
      return { barClass: "bg-cyan-500", textClass: "text-cyan-400" }
    return { barClass: "bg-amber-500", textClass: "text-amber-400" }
  }

  const { barClass, textClass } = getScoreStyle()

  return (
    <div className="flex items-center gap-2">
      <div className="relative h-1.5 w-12 overflow-hidden rounded-full bg-muted">
        <div
          className={cn(
            "absolute inset-y-0 left-0 rounded-full transition-all",
            barClass
          )}
          style={{ width: `${score}%` }}
        />
      </div>
      <span className={cn("min-w-[28px] text-xs font-semibold", textClass)}>
        {score}
      </span>
    </div>
  )
}

function SourceBadge({ source }: { source: LeadSource }) {
  const sourceConfig: Record<
    LeadSource,
    {
      icon: typeof Linkedin01Icon
      label: string
      bgClass: string
      textClass: string
    }
  > = {
    linkedin: {
      icon: Linkedin01Icon,
      label: "LinkedIn",
      bgClass: "bg-blue-500/10",
      textClass: "text-blue-400",
    },
    google: {
      icon: Search01Icon,
      label: "Google",
      bgClass: "bg-red-500/10",
      textClass: "text-red-400",
    },
    referral: {
      icon: UserMultiple02Icon,
      label: "Referral",
      bgClass: "bg-violet-500/10",
      textClass: "text-violet-400",
    },
    website: {
      icon: Globe02Icon,
      label: "Website",
      bgClass: "bg-cyan-500/10",
      textClass: "text-cyan-400",
    },
    "cold-call": {
      icon: CallIcon,
      label: "Cold Call",
      bgClass: "bg-orange-500/10",
      textClass: "text-orange-400",
    },
  }

  const config = sourceConfig[source]

  return (
    <div
      className={cn(
        "flex w-fit items-center gap-1.5 rounded-md px-2 py-1",
        config.bgClass
      )}
    >
      <HugeiconsIcon
        icon={config.icon}
        className={cn("size-3", config.textClass)}
      />
      <span className={cn("text-xs font-medium", config.textClass)}>
        {config.label}
      </span>
    </div>
  )
}

export function LeadsTable() {
  const {
    searchQuery,
    typeFilter,
    statusFilter,
    sourceFilter,
    setSearchQuery,
    setTypeFilter,
    setStatusFilter,
    setSourceFilter,
    clearFilters,
  } = useDashboardStore()

  const [sortField, setSortField] = useState<SortField>("name")
  const [sortOrder, setSortOrder] = useState<SortOrder>("asc")
  const [selectedLeads, setSelectedLeads] = useState<string[]>([])
  const [currentPage, setCurrentPage] = useState(1)
  const [itemsPerPage, setItemsPerPage] = useState(10)

  const filteredAndSortedLeads = useMemo(() => {
    const result = leads.filter((lead) => {
      const matchesSearch =
        searchQuery === "" ||
        lead.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        lead.email.toLowerCase().includes(searchQuery.toLowerCase())

      const matchesType = typeFilter === "all" || lead.type === typeFilter
      const matchesStatus =
        statusFilter === "all" || lead.status === statusFilter
      const matchesSource =
        sourceFilter === "all" || lead.source === sourceFilter

      return matchesSearch && matchesType && matchesStatus && matchesSource
    })

    result.sort((a, b) => {
      let comparison = 0
      switch (sortField) {
        case "name":
          comparison = a.name.localeCompare(b.name)
          break
        case "email":
          comparison = a.email.localeCompare(b.email)
          break
        case "followUp":
          comparison = a.followUp.localeCompare(b.followUp)
          break
        case "status":
          comparison = a.status.localeCompare(b.status)
          break
        case "score":
          comparison = a.score - b.score
          break
      }
      return sortOrder === "asc" ? comparison : -comparison
    })

    return result
  }, [
    searchQuery,
    typeFilter,
    statusFilter,
    sourceFilter,
    sortField,
    sortOrder,
  ])

  const totalPages = Math.ceil(filteredAndSortedLeads.length / itemsPerPage)
  const paginatedLeads = useMemo(() => {
    const startIndex = (currentPage - 1) * itemsPerPage
    return filteredAndSortedLeads.slice(startIndex, startIndex + itemsPerPage)
  }, [filteredAndSortedLeads, currentPage, itemsPerPage])

  const toggleSort = (field: SortField) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc")
    } else {
      setSortField(field)
      setSortOrder("asc")
    }
  }

  const toggleSelectAll = () => {
    if (selectedLeads.length === paginatedLeads.length) {
      setSelectedLeads([])
    } else {
      setSelectedLeads(paginatedLeads.map((lead) => lead.id))
    }
  }

  const toggleSelectLead = (id: string) => {
    setSelectedLeads((prev) =>
      prev.includes(id) ? prev.filter((i) => i !== id) : [...prev, id]
    )
  }

  const hasActiveFilters =
    searchQuery !== "" ||
    typeFilter !== "all" ||
    statusFilter !== "all" ||
    sourceFilter !== "all"

  const handlePageChange = (page: number) => {
    setCurrentPage(page)
    setSelectedLeads([])
  }

  const handleItemsPerPageChange = (value: string | null) => {
    if (value) {
      setItemsPerPage(Number(value))
      setCurrentPage(1)
      setSelectedLeads([])
    }
  }

  return (
    <div className="overflow-hidden rounded-xl border bg-card text-card-foreground">
      <div className="flex flex-col gap-3 border-b px-4 py-3.5 sm:flex-row sm:items-center sm:justify-between">
        <div className="flex items-center gap-3">
          <h3 className="text-base font-medium">Lead Management</h3>
          <div className="hidden h-5 w-px bg-border sm:block" />
          <div className="hidden items-center gap-2 sm:flex">
            <div className="relative">
              <HugeiconsIcon
                icon={Search01Icon}
                className="pointer-events-none absolute top-1/2 left-2.5 z-10 size-3.5 -translate-y-1/2 text-muted-foreground"
              />
              <Input
                placeholder="Search"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="h-8 w-[200px] border-border/50 bg-muted/50 pl-8 text-sm"
              />
            </div>

            <DropdownMenu>
              <DropdownMenuTrigger
                render={
                  <Button
                    variant="outline"
                    size="sm"
                    className="h-8 gap-1.5 border-border/50 bg-muted/50"
                  >
                    <HugeiconsIcon
                      icon={SlidersHorizontalIcon}
                      className="size-3.5"
                    />
                    <span>Filter</span>
                    {hasActiveFilters && (
                      <span className="size-1.5 rounded-full bg-primary" />
                    )}
                  </Button>
                }
              />
              <DropdownMenuContent align="start" className="w-48">
                <DropdownMenuGroup>
                  <div className="px-2 py-1.5">
                    <p className="mb-1.5 text-xs font-medium text-muted-foreground">
                      Type
                    </p>
                    <div className="space-y-1">
                      <DropdownMenuCheckboxItem
                        checked={typeFilter === "all"}
                        onCheckedChange={() => setTypeFilter("all")}
                      >
                        All Types
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={typeFilter === "cold"}
                        onCheckedChange={() => setTypeFilter("cold")}
                      >
                        <HugeiconsIcon
                          icon={SnowIcon}
                          className="mr-1.5 size-3 text-cyan-400"
                        />
                        Cold
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={typeFilter === "warm"}
                        onCheckedChange={() => setTypeFilter("warm")}
                      >
                        <HugeiconsIcon
                          icon={FireIcon}
                          className="mr-1.5 size-3 text-pink-400"
                        />
                        Warm
                      </DropdownMenuCheckboxItem>
                    </div>
                  </div>
                </DropdownMenuGroup>
                <DropdownMenuSeparator />
                <DropdownMenuGroup>
                  <div className="px-2 py-1.5">
                    <p className="mb-1.5 text-xs font-medium text-muted-foreground">
                      Status
                    </p>
                    <div className="space-y-1">
                      <DropdownMenuCheckboxItem
                        checked={statusFilter === "all"}
                        onCheckedChange={() => setStatusFilter("all")}
                      >
                        All Status
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={statusFilter === "closed"}
                        onCheckedChange={() => setStatusFilter("closed")}
                      >
                        <HugeiconsIcon
                          icon={Tick01Icon}
                          className="mr-1.5 size-3 text-emerald-400"
                        />
                        Closed
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={statusFilter === "lost"}
                        onCheckedChange={() => setStatusFilter("lost")}
                      >
                        <HugeiconsIcon
                          icon={Cancel01Icon}
                          className="mr-1.5 size-3 text-amber-400"
                        />
                        Lost
                      </DropdownMenuCheckboxItem>
                    </div>
                  </div>
                </DropdownMenuGroup>
                <DropdownMenuSeparator />
                <DropdownMenuGroup>
                  <div className="px-2 py-1.5">
                    <p className="mb-1.5 text-xs font-medium text-muted-foreground">
                      Source
                    </p>
                    <div className="space-y-1">
                      <DropdownMenuCheckboxItem
                        checked={sourceFilter === "all"}
                        onCheckedChange={() => setSourceFilter("all")}
                      >
                        All Sources
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={sourceFilter === "linkedin"}
                        onCheckedChange={() => setSourceFilter("linkedin")}
                      >
                        <HugeiconsIcon
                          icon={Linkedin01Icon}
                          className="mr-1.5 size-3 text-blue-400"
                        />
                        LinkedIn
                      </DropdownMenuCheckboxItem>
                      <DropdownMenuCheckboxItem
                        checked={sourceFilter === "google"}
                        onCheckedChange={() => setSourceFilter("google")}
                      >
                        <HugeiconsIcon
                          icon={Search01Icon}
                          className="mr-1.5 size-3 text-red-400"
