export function TopPerformers() {
  const [sortBy, setSortBy] = useState<SortBy>("score_desc")
  const [period, setPeriod] = useState<Period>("month")

  const maxScore = useMemo(() => {
    return Math.max(...topPerformers.map((p) => p.score))
  }, [])

  const sortedPerformers = useMemo(() => {
    const performers = [...topPerformers]
    switch (sortBy) {
      case "score_desc":
        return performers.sort((a, b) => b.score - a.score)
      case "score_asc":
        return performers.sort((a, b) => a.score - b.score)
      case "name_asc":
        return performers.sort((a, b) => a.name.localeCompare(b.name))
      case "name_desc":
        return performers.sort((a, b) => b.name.localeCompare(a.name))
      default:
        return performers
    }
  }, [sortBy])

  return (
    <div className="w-full shrink-0 rounded-lg border bg-card text-card-foreground lg:w-[332px]">
      <div className="flex items-center justify-between border-b border-border/50 p-4">
        <h3 className="text-sm font-medium sm:text-base">Top Agents</h3>
        <div className="flex items-center gap-1">
          <HugeiconsIcon
            icon={Award01Icon}
            className="size-4 text-muted-foreground"
          />
          <DropdownMenu>
            <DropdownMenuTrigger
              render={
                <Button variant="ghost" size="icon" className="size-7">
                  <HugeiconsIcon icon={MoreHorizontalIcon} className="size-4" />
                </Button>
              }
            />
            <DropdownMenuContent align="end" className="w-44">
              <DropdownMenuSub>
                <DropdownMenuSubTrigger>Time Period</DropdownMenuSubTrigger>
                <DropdownMenuSubContent>
                  <DropdownMenuGroup>
                    {(Object.keys(periodLabels) as Period[]).map((p) => (
                      <DropdownMenuItem key={p} onClick={() => setPeriod(p)}>
                        {periodLabels[p]} {period === p && "✓"}
                      </DropdownMenuItem>
                    ))}
                  </DropdownMenuGroup>
                </DropdownMenuSubContent>
              </DropdownMenuSub>
              <DropdownMenuSub>
                <DropdownMenuSubTrigger>Sort By</DropdownMenuSubTrigger>
                <DropdownMenuSubContent>
                  <DropdownMenuGroup>
                    <DropdownMenuItem onClick={() => setSortBy("score_desc")}>
                      Score (High to Low) {sortBy === "score_desc" && "✓"}
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => setSortBy("score_asc")}>
                      Score (Low to High) {sortBy === "score_asc" && "✓"}
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => setSortBy("name_asc")}>
                      Name (A to Z) {sortBy === "name_asc" && "✓"}
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => setSortBy("name_desc")}>
                      Name (Z to A) {sortBy === "name_desc" && "✓"}
                    </DropdownMenuItem>
                  </DropdownMenuGroup>
                </DropdownMenuSubContent>
              </DropdownMenuSub>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                onClick={() => {
                  setSortBy("score_desc")
                  setPeriod("month")
                }}
              >
                Reset to Default
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
      <div className="space-y-4 p-4">
        {sortedPerformers.map((performer, index) => {
          const style = barStyles[index % barStyles.length]
          const progressWidth = (performer.score / maxScore) * 100
          const isFirst = index === 0

          return (
            <div key={performer.id} className="flex items-center gap-3">
              <Avatar className="size-10">
                <AvatarImage src={performer.avatar} />
                <AvatarFallback>
                  {performer.name
                    .split(" ")
                    .map((n) => n[0])
                    .join("")}
                </AvatarFallback>
              </Avatar>
              <div className="relative flex-1">
                <div
                  className={cn(
                    "relative h-[42px] overflow-hidden rounded-lg border",
                    style.borderColor,
                    style.isDashed ? "border-dashed" : "border-solid"
                  )}
                >
                  <div
                    className={cn(
                      "absolute inset-0 transition-all duration-300",
                      style.bgGradient
                    )}
                    style={{
                      width: `${Math.max(progressWidth, 30)}%`,
                    }}
                  />
                  <div className="absolute top-1/2 left-2 flex -translate-y-1/2 items-center gap-1.5 rounded-md border border-border bg-card/90 px-2 py-1 shadow-sm dark:bg-neutral-900/90">
                    {isFirst ? (
                      <HugeiconsIcon
                        icon={StarIcon}
                        className="size-3.5 fill-amber-400 text-amber-400"
                      />
                    ) : (
                      <HugeiconsIcon
                        icon={UserIcon}
                        className="size-3.5 text-muted-foreground"
                      />
                    )}
                    <span
                      className={cn(
                        "text-sm font-medium",
                        isFirst ? "text-foreground" : "text-muted-foreground"
                      )}
                    >
                      {performer.score}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          )
        })}
      </div>
    </div>
  )
}

