"use client"

import { format } from "date-fns"

import { Avatar, AvatarFallback, AvatarImage } from "@loveui/ui/ui/avatar"

import { cn } from "../../lib/utils"
import { useEmailsStore } from "../../store/emails-store"
import { VerifiedIcon } from "../ui/verified-icon"

interface EmailListProps {
  onEmailClick?: (emailId: string) => void
}

export function EmailList({ onEmailClick }: EmailListProps) {
  const { emails, selectedEmailId, selectEmail } = useEmailsStore()

  const handleEmailClick = (emailId: string) => {
    selectEmail(emailId)
    onEmailClick?.(emailId)
  }

  if (emails.length === 0) {
    return (
      <div className="flex h-full items-center justify-center bg-card">
        <div className="text-center text-muted-foreground">
          <p>No alerts found</p>
        </div>
      </div>
    )
  }

  return (
    <div className="flex h-full flex-col overflow-hidden bg-card">
      <div className="flex items-center justify-between border-b border-border px-5 py-3">
        <div className="flex items-center gap-2">
          <div className="size-3.5 rounded border border-border" />
          <p className="text-sm font-medium text-foreground">Active Alerts</p>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto">
        {emails.map((email) => {
          const isSelected = selectedEmailId === email.id
          const isRead = email.read

          return (
            <button
              key={email.id}
              onClick={() => handleEmailClick(email.id)}
              className={cn(
                "flex w-full gap-2.5 border-b border-border p-4 text-left transition-colors hover:bg-muted/70",
                isSelected && "bg-muted",
                !isRead && "bg-muted/50"
              )}
            >
              <div className={cn("flex w-full gap-2.5")}>
                <Avatar className="mt-1.5 size-7 shrink-0">
                  <AvatarImage src={email.from.avatar} alt={email.from.name} />
                  <AvatarFallback>{email.from.name.charAt(0)}</AvatarFallback>
                </Avatar>

                <div className="flex-1 overflow-hidden">
                  <div className="flex items-start justify-between gap-2.5">
                    <div className="flex-1 overflow-hidden">
                      <div className="flex items-center gap-1">
                        <p
                          className={cn(
                            "truncate text-[14px] tracking-tight",
                            !isRead || isSelected
                              ? "font-medium text-foreground"
                              : "text-foreground"
                          )}
                        >
                          {email.from.name}
                        </p>
                        {email.from.verified && (
                          <VerifiedIcon className="size-4 shrink-0" />
                        )}
                      </div>
                      <p className="truncate text-[12px] tracking-tight text-[#38bdf8]">
                        {email.from.email}
                      </p>
                    </div>
                    <p
                      className={cn(
                        "shrink-0 text-[12px] tracking-tight",
                        !isRead
                          ? "font-medium text-foreground"
                          : "text-foreground"
                      )}
                    >
                      {format(email.date, "MMM dd")}
                    </p>
                  </div>

                  <div className="mt-1.5 space-y-0.5">
                    <p className="line-clamp-2 text-[12px] leading-relaxed tracking-tight text-foreground">
                      {email.body}
                    </p>
                  </div>
                </div>
              </div>
            </button>
          )
        })}
      </div>
    </div>
  )
}

"use client"

import {
  IconAlertTriangle,
  IconArchive,
  IconAsterisk,
  IconFileText,
  IconInbox,
  IconMailPlus,
  IconMenu2,
  IconSend,
  IconStar,
  IconTrash,
} from "@tabler/icons-react"

import { Button } from "@loveui/ui/ui/button"
import { Separator } from "@loveui/ui/ui/separator"

import { cn } from "../../lib/utils"
import { useEmailsStore } from "../../store/emails-store"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "../ui/dropdown-menu"

export function EmailsHorizontalNav() {
  const { emails, currentFolder, setFolder } = useEmailsStore()

  const mainFolders = [
    {
      id: "inbox",
      label: "Active Alerts",
      icon: IconInbox,
      count: emails.length.toString() as string | undefined,
    },
    { id: "sent", label: "Escalated", icon: IconSend, count: undefined },
    {
      id: "drafts",
      label: "Draft Notes",
      icon: IconFileText,
      count: undefined,
    },
    { id: "starred", label: "Watchlist", icon: IconStar, count: undefined },
    { id: "archive", label: "Resolved", icon: IconArchive, count: undefined },
  ]

  const additionalFolders = [
    { id: "deleted", label: "Discarded", icon: IconTrash, count: undefined },
    { id: "spam", label: "Noise", icon: IconAlertTriangle, count: undefined },
    { id: "junk", label: "Low Priority", icon: IconAsterisk, count: undefined },
  ]

  const allFolders = [...mainFolders, ...additionalFolders]

  return (
    <div className="flex h-[54px] items-center border-b border-border bg-background px-3 md:px-4">
      <div className="flex w-full items-center gap-2 md:gap-3">
        <Button
          size="sm"
          className="relative h-[30px] shrink-0 overflow-hidden bg-linear-to-r from-white to-white hover:opacity-90"
        >
          <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_top_right,rgba(205,175,250,1),transparent_50%),radial-gradient(ellipse_at_bottom_right,rgba(129,169,248,1),transparent_50%),radial-gradient(ellipse_at_top_left,rgba(247,203,191,1),transparent_50%),radial-gradient(ellipse_at_bottom_left,rgba(164,252,245,1),transparent_50%)]" />
          <IconMailPlus className="relative size-4 text-black" stroke={1.5} />
          <span className="relative hidden text-black sm:inline">Escalate</span>
        </Button>

        <Separator orientation="vertical" className="hidden h-5 sm:block" />

        <div className="hidden flex-1 items-center gap-2.5 2xl:flex">
          {allFolders.map((folder) => {
            const Icon = folder.icon
            const isActive = currentFolder === folder.id

            return (
              <Button
                key={folder.id}
                variant="ghost"
                size="sm"
                onClick={() => setFolder(folder.id as string)}
                className={cn(
                  "h-[30px] gap-1.5",
                  isActive && "bg-muted text-foreground hover:bg-muted"
                )}
              >
                <Icon className="size-4" stroke={1.5} />
                <span className="text-[13px]">{folder.label}</span>
                {folder.count && isActive && (
                  <span className="text-[12px]">{folder.count}</span>
                )}
              </Button>
            )
          })}
        </div>

        <div className="hidden flex-1 items-center gap-2.5 xl:flex 2xl:hidden">
          {mainFolders.map((folder) => {
            const Icon = folder.icon
            const isActive = currentFolder === folder.id

            return (
              <Button
                key={folder.id}
                variant="ghost"
                size="sm"
                onClick={() => setFolder(folder.id as string)}
                className={cn(
                  "h-[30px] gap-1.5",
                  isActive && "bg-muted text-foreground hover:bg-muted"
                )}
              >
                <Icon className="size-4" stroke={1.5} />
                <span className="text-[13px]">{folder.label}</span>
                {folder.count && isActive && (
                  <span className="text-[12px]">{folder.count}</span>
                )}
              </Button>
            )
          })}

          <Separator orientation="vertical" className="h-5" />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <IconMenu2 className="size-4" stroke={1.5} />
                <span className="text-[13px]">More</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {additionalFolders.map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                )
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="hidden flex-1 items-center gap-2.5 lg:flex xl:hidden">
          {mainFolders.slice(0, 3).map((folder) => {
            const Icon = folder.icon
            const isActive = currentFolder === folder.id

            return (
              <Button
                key={folder.id}
                variant="ghost"
                size="sm"
                onClick={() => setFolder(folder.id as string)}
                className={cn(
                  "h-[30px] gap-1.5",
                  isActive && "bg-muted text-foreground hover:bg-muted"
                )}
              >
                <Icon className="size-4" stroke={1.5} />
                <span className="text-[13px]">{folder.label}</span>
                {folder.count && isActive && (
                  <span className="text-[12px]">{folder.count}</span>
                )}
              </Button>
            )
          })}

          <Separator orientation="vertical" className="h-5" />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <IconMenu2 className="size-4" stroke={1.5} />
                <span className="text-[13px]">More</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.slice(3).map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                )
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                )
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="hidden flex-1 items-center gap-2 md:flex lg:hidden">
          {mainFolders.slice(0, 2).map((folder) => {
            const Icon = folder.icon
            const isActive = currentFolder === folder.id

            return (
              <Button
                key={folder.id}
                variant="ghost"
                size="sm"
                onClick={() => setFolder(folder.id as string)}
                className={cn(
                  "h-[30px] gap-1.5",
                  isActive && "bg-muted text-foreground hover:bg-muted"
                )}
              >
                <Icon className="size-4" stroke={1.5} />
                <span className="text-[13px]">{folder.label}</span>
              </Button>
            )
          })}

          <Separator orientation="vertical" className="h-5" />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <IconMenu2 className="size-4" stroke={1.5} />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.slice(2).map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                    {folder.count && isActive && (
                      <span className="ml-auto text-xs text-muted-foreground">
                        {folder.count}
                      </span>
                    )}
                  </DropdownMenuItem>
                )
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                )
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="flex flex-1 items-center gap-2 md:hidden">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <IconMenu2 className="size-4" stroke={1.5} />
                <span className="text-[13px]">Folders</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                    {folder.count && isActive && (
                      <span className="ml-auto text-xs text-muted-foreground">
                        {folder.count}
                      </span>
                    )}
                  </DropdownMenuItem>
                )
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon
                const isActive = currentFolder === folder.id

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as string)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" stroke={1.5} />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                )
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </div>
  )
}

"use client"

import * as React from "react"
import { ThemeProvider as NextThemesProvider } from "next-themes"

export function ThemeProvider({
  children,
  ...props
}: React.ComponentProps<typeof NextThemesProvider>) {
  return <NextThemesProvider {...props}>{children}</NextThemesProvider>
}

"use client"

import * as React from "react"
import { AlertDialog as AlertDialogPrimitive } from "radix-ui"

import { cn } from "../../lib/utils"
import { Button } from "./button"

function AlertDialog({
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Root>) {
  return <AlertDialogPrimitive.Root data-slot="alert-dialog" {...props} />
}

function AlertDialogTrigger({
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Trigger>) {
  return (
    <AlertDialogPrimitive.Trigger data-slot="alert-dialog-trigger" {...props} />
  )
}

function AlertDialogPortal({
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Portal>) {
  return (
    <AlertDialogPrimitive.Portal data-slot="alert-dialog-portal" {...props} />
  )
}

function AlertDialogOverlay({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Overlay>) {
  return (
    <AlertDialogPrimitive.Overlay
      data-slot="alert-dialog-overlay"
      className={cn(
        "data-open:animate-in data-closed:animate-out data-closed:fade-out-0 data-open:fade-in-0 fixed inset-0 z-50 bg-black/10 duration-100 supports-backdrop-filter:backdrop-blur-xs",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogContent({
  className,
  size = "default",
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Content> & {
  size?: "default" | "sm"
}) {
  return (
    <AlertDialogPortal>
      <AlertDialogOverlay />
      <AlertDialogPrimitive.Content
        data-slot="alert-dialog-content"
        data-size={size}
        className={cn(
          "data-open:animate-in data-closed:animate-out data-closed:fade-out-0 data-open:fade-in-0 data-closed:zoom-out-95 data-open:zoom-in-95 group/alert-dialog-content fixed top-1/2 left-1/2 z-50 grid w-full -translate-x-1/2 -translate-y-1/2 gap-6 rounded-xl bg-background p-6 ring-1 ring-foreground/10 duration-100 outline-none data-[size=default]:max-w-xs data-[size=sm]:max-w-xs data-[size=default]:sm:max-w-lg",
          className
        )}
        {...props}
      />
    </AlertDialogPortal>
  )
}

function AlertDialogHeader({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="alert-dialog-header"
      className={cn(
        "grid grid-rows-[auto_1fr] place-items-center gap-1.5 text-center has-data-[slot=alert-dialog-media]:grid-rows-[auto_auto_1fr] has-data-[slot=alert-dialog-media]:gap-x-6 sm:group-data-[size=default]/alert-dialog-content:place-items-start sm:group-data-[size=default]/alert-dialog-content:text-left sm:group-data-[size=default]/alert-dialog-content:has-data-[slot=alert-dialog-media]:grid-rows-[auto_1fr]",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogFooter({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="alert-dialog-footer"
      className={cn(
        "flex flex-col-reverse gap-2 group-data-[size=sm]/alert-dialog-content:grid group-data-[size=sm]/alert-dialog-content:grid-cols-2 sm:flex-row sm:justify-end",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogMedia({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="alert-dialog-media"
      className={cn(
        "mb-2 inline-flex size-16 items-center justify-center rounded-md bg-muted sm:group-data-[size=default]/alert-dialog-content:row-span-2 *:[svg:not([class*='size-'])]:size-8",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogTitle({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Title>) {
  return (
    <AlertDialogPrimitive.Title
      data-slot="alert-dialog-title"
      className={cn(
        "text-lg font-medium sm:group-data-[size=default]/alert-dialog-content:group-has-data-[slot=alert-dialog-media]/alert-dialog-content:col-start-2",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogDescription({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Description>) {
  return (
    <AlertDialogPrimitive.Description
      data-slot="alert-dialog-description"
      className={cn(
        "text-sm text-balance text-muted-foreground md:text-pretty *:[a]:underline *:[a]:underline-offset-3 *:[a]:hover:text-foreground",
        className
      )}
      {...props}
    />
  )
}

function AlertDialogAction({
  className,
  variant = "default",
  size = "default",
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Action> &
  Pick<React.ComponentProps<typeof Button>, "variant" | "size">) {
  return (
    <Button variant={variant} size={size} asChild>
      <AlertDialogPrimitive.Action
        data-slot="alert-dialog-action"
        className={cn(className)}
        {...props}
      />
    </Button>
  )
}

function AlertDialogCancel({
  className,
  variant = "outline",
  size = "default",
  ...props
}: React.ComponentProps<typeof AlertDialogPrimitive.Cancel> &
  Pick<React.ComponentProps<typeof Button>, "variant" | "size">) {
  return (
    <Button variant={variant} size={size} asChild>
      <AlertDialogPrimitive.Cancel
        data-slot="alert-dialog-cancel"
        className={cn(className)}
        {...props}
      />
    </Button>
  )
}

export {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogMedia,
  AlertDialogOverlay,
  AlertDialogPortal,
  AlertDialogTitle,
  AlertDialogTrigger,
}

"use client"

import * as React from "react"

import { cn } from "../../lib/utils"

const Avatar = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "relative flex size-10 shrink-0 overflow-hidden rounded-full",
      className
    )}
    {...props}
  />
))
Avatar.displayName = "Avatar"

const AvatarImage = React.forwardRef<
  HTMLImageElement,
  React.ImgHTMLAttributes<HTMLImageElement>
>(({ className, src, alt, ...props }, ref) => {
  const [hasError, setHasError] = React.useState(false)

  if (!src || hasError) {
    return null
  }

  return (
    <img
      ref={ref}
      src={src}
      alt={alt}
      onError={() => setHasError(true)}
      className={cn("aspect-square size-full object-cover", className)}
      {...props}
    />
  )
})
AvatarImage.displayName = "AvatarImage"

const AvatarFallback = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "flex size-full items-center justify-center rounded-full bg-muted text-sm font-medium",
      className
    )}
    {...props}
  />
))
AvatarFallback.displayName = "AvatarFallback"

export { Avatar, AvatarImage, AvatarFallback }

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { Slot } from "radix-ui"

import { cn } from "../../lib/utils"

const badgeVariants = cva(
  "group/badge inline-flex h-5 w-fit shrink-0 items-center justify-center gap-1 overflow-hidden rounded-4xl border border-transparent px-2 py-0.5 text-xs font-medium whitespace-nowrap transition-all transition-colors focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 has-data-[icon=inline-end]:pr-1.5 has-data-[icon=inline-start]:pl-1.5 aria-invalid:border-destructive aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 [&>svg]:pointer-events-none [&>svg]:size-3!",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground [a]:hover:bg-primary/80",
        secondary:
          "bg-secondary text-secondary-foreground [a]:hover:bg-secondary/80",
        destructive:
          "bg-destructive/10 text-destructive focus-visible:ring-destructive/20 dark:bg-destructive/20 dark:focus-visible:ring-destructive/40 [a]:hover:bg-destructive/20",
        outline:
          "border-border text-foreground [a]:hover:bg-muted [a]:hover:text-muted-foreground",
        ghost:
          "hover:bg-muted hover:text-muted-foreground dark:hover:bg-muted/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

function Badge({
  className,
  variant = "default",
  asChild = false,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot.Root : "span"

  return (
    <Comp
      data-slot="badge"
      data-variant={variant}
      className={cn(badgeVariants({ variant }), className)}
      {...(props as any)}
    />
  )
}

export { Badge, badgeVariants }

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { Slot } from "radix-ui"

import { cn } from "../../lib/utils"

const buttonVariants = cva(
  "group/button inline-flex shrink-0 items-center justify-center rounded-md border border-transparent bg-clip-padding text-sm font-medium whitespace-nowrap transition-all outline-none select-none focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50 disabled:pointer-events-none disabled:opacity-50 aria-invalid:border-destructive aria-invalid:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:border-destructive/50 dark:aria-invalid:ring-destructive/40 [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4",
