"use client"

import {
  File01Icon,
  FileAttachmentIcon,
  FolderZipIcon,
  Image01Icon,
  Video01Icon,
} from "@hugeicons/core-free-icons"
import { HugeiconsIcon } from "@hugeicons/react"

import { storageData } from "../../mock-data/files"

const iconMap = {
  Images: Image01Icon,
  Videos: Video01Icon,
  Documents: File01Icon,
  Archives: FolderZipIcon,
  Other: FileAttachmentIcon,
}

export function StorageCards() {
  return (
    <div className="grid grid-cols-2 gap-3 sm:grid-cols-3 lg:grid-cols-5">
      {storageData.breakdown.map((item) => {
        const Icon =
          iconMap[item.type as keyof typeof iconMap] || FileAttachmentIcon
        const percentage = ((item.size / storageData.total) * 100).toFixed(0)

        return (
          <div
            key={item.type}
            className="group cursor-pointer rounded-xl border bg-card p-4 transition-colors hover:bg-accent/50"
          >
            <div
              className="mb-3 flex size-10 items-center justify-center rounded-lg"
              style={{ backgroundColor: `${item.color}15` }}
            >
              <HugeiconsIcon
                icon={Icon}
                className="size-5"
                style={{ color: item.color }}
              />
            </div>
            <p className="mb-0.5 text-sm font-medium">{item.type}</p>
            <div className="flex items-center justify-between">
              <span className="text-xs text-muted-foreground">
                {item.size} GB
              </span>
              <span className="text-xs text-muted-foreground">
                {percentage}%
              </span>
            </div>
          </div>
        )
      })}
    </div>
  )
}

"use client"

import { storageData } from "../../mock-data/files"

export function StorageOverview() {
  const usedPercentage = (storageData.used / storageData.total) * 100

  return (
    <div className="rounded-xl border bg-card p-4">
      <div className="mb-4 flex items-center justify-between">
        <h3 className="text-sm font-medium">Storage Overview</h3>
        <button className="text-xs font-medium text-violet-500 transition-colors hover:text-violet-600">
          Upgrade
        </button>
      </div>

      <div className="relative mb-3 h-3 overflow-hidden rounded-full bg-muted">
        <div className="absolute inset-0 flex overflow-hidden rounded-full">
          {storageData.breakdown.map((item, index) => {
            const width = (item.size / storageData.used) * usedPercentage
            const isFirst = index === 0
            const isLast = index === storageData.breakdown.length - 1
            return (
              <div
                key={item.type}
                className={`h-full ${isFirst ? "rounded-l-full" : ""} ${isLast ? "rounded-r-full" : ""}`}
                style={{
                  width: `${width}%`,
                  backgroundColor: item.color,
                }}
              />
            )
          })}
        </div>
      </div>

      <div className="mb-4 flex items-center justify-between text-sm">
        <span className="text-muted-foreground">
          {storageData.used} GB of {storageData.total} GB used
        </span>
        <span className="font-medium">{usedPercentage.toFixed(0)}%</span>
      </div>

      <div className="grid grid-cols-2 gap-2">
        {storageData.breakdown.map((item) => (
          <div
            key={item.type}
            className="flex items-center gap-2 rounded-lg bg-muted/50 p-2"
          >
            <div
              className="size-2.5 shrink-0 rounded-full"
              style={{ backgroundColor: item.color }}
            />
            <span className="truncate text-xs text-muted-foreground">
              {item.type}
            </span>
            <span className="ml-auto text-xs font-medium">{item.size} GB</span>
          </div>
        ))}
      </div>
    </div>
  )
}

